import Link from 'next/link';
import { useState, useEffect, useRef } from 'react';
import Router from 'next/router';
import dynamic from 'next/dynamic';
import { withRouter } from 'next/router';
import { getCookie, isAuth } from '../../actions/auth';
import { getCategories } from '../../actions/category';
import { getTags } from '../../actions/tag';
import { singleBlog, updateBlog } from '../../actions/blog';
const ReactQuill = dynamic(() => import('react-quill'), { ssr: false });
import '../../node_modules/react-quill/dist/quill.snow.css';
import { QuillModules, QuillFormats } from '../../helpers/quill';
import { API } from '../../config';

import {
	Form,
	FormGroup,
	FormControl,
	ControlLabel,
	HelpBlock,
	TagPicker,
	FlexboxGrid,
	SelectPicker,
	Uploader,
	Icon,
	Loader,
	Button,
	ButtonToolbar,
	Modal
} from 'rsuite';

const BlogUpdate = ({ router }) => {
	const [ body, setBody ] = useState('');

	const [ categories, setCategories ] = useState([]);
	const [ tags, setTags ] = useState([]);

	const [ checkedCat, setCheckedCat ] = useState([]); // categories
	const [ checkedTag, setCheckedTag ] = useState([]); // tags

	const [ values, setValues ] = useState({
		title: '',
		error: '',
		success: '',
		formData: '',
		title: '',
        body: '',
        initial: true
	});

	const { error, success, formData, title, initial } = values;
	const token = getCookie('token');

	useEffect(
		() => {
			setValues({ ...values, formData: new FormData() });
			initBlog();
			initCategories();
			initTags();
		},
		[ router ]
	);

	const initBlog = () => {
		if (router.query.slug) {
			singleBlog(router.query.slug).then((data) => {
				if (data.error) {
					console.log(data.error);
				} else {
					setValues({ ...values, title: data.title });
					setBody(data.body);
					setCategoriesArray(data.categories);
					setTagsArray(data.tags);
				}
			});
		}
	};

	const setCategoriesArray = (blogCategories) => {
		setCheckedCat(blogCategories[0]._id);
	};

	const setTagsArray = (blogTags) => {
		let ta = [];
		blogTags.map((t, i) => {
			ta.push(t._id);
		});
		setCheckedTag(ta);
	};

	const initCategories = () => {
		getCategories().then((data) => {
			if (data.error) {
				setValues({ ...values, error: data.error });
			} else {
				const items = [];
				data.forEach((item, i) => {
					items[i] = {
						key: i,
						value: item._id,
						label: item.name
					};
				});
				setCategories(items);
			}
		});
	};

	const initTags = () => {
		getTags().then((data) => {
			if (data.error) {
				setValues({ ...values, error: data.error });
			} else {
				const items = [];
				data.forEach((item, i) => {
					items[i] = {
						key: i,
						value: item._id,
						label: item.name
					};
				});
				setTags(items);
			}
		});
	};

	const handleToggle = (c) => () => {
		setValues({ ...values, error: '' });
		// return the first index or -1
		const clickedCategory = checked.indexOf(c);
		const all = [ ...checked ];

		if (clickedCategory === -1) {
			all.push(c);
		} else {
			all.splice(clickedCategory, 1);
		}
		console.log(all);
		setChecked(all);
		formData.set('categories', all);
	};

	const handleTagsToggle = (t) => () => {
		setValues({ ...values, error: '' });
		// return the first index or -1
		const clickedTag = checkedTag.indexOf(t);
		const all = [ ...checkedTag ];

		if (clickedTag === -1) {
			all.push(t);
		} else {
			all.splice(clickedTag, 1);
		}
		console.log(all);
		setCheckedTag(all);
		formData.set('tags', all);
	};

	const findOutCategory = (c) => {
		const result = checked.indexOf(c);
		if (result !== -1) {
			return true;
		} else {
			return false;
		}
	};

	const findOutTag = (t) => {
		const result = checkedTag.indexOf(t);
		if (result !== -1) {
			return true;
		} else {
			return false;
		}
	};

	const showCategories = () => {
		return (
			categories &&
			categories.map((c, i) => (
				<li key={i} className="list-unstyled">
					<input
						onChange={handleToggle(c._id)}
						checked={findOutCategory(c._id)}
						type="checkbox"
						className="mr-2"
					/>
					<label className="form-check-label">{c.name}</label>
				</li>
			))
		);
	};

	const showTags = () => {
		return (
			tags &&
			tags.map((t, i) => (
				<li key={i} className="list-unstyled">
					<input
						onChange={handleTagsToggle(t._id)}
						checked={findOutTag(t._id)}
						type="checkbox"
						className="mr-2"
					/>
					<label className="form-check-label">{t.name}</label>
				</li>
			))
		);
	};

	const handleChange = (name) => (e) => {
		// console.log(e.target.value);
		const value = name === 'photo' ? e.target.files[0] : e.target.value;
		formData.set(name, value);
		setValues({ ...values, [name]: value, formData, error: '' });
	};

	const handleBody = (e) => {
		setBody(e);
		formData.set('body', e);
	};

	const editBlog = (e) => {
		e.preventDefault();
		updateBlog(formData, token, router.query.slug).then((data) => {
			if (data.error) {
				setValues({ ...values, error: data.error });
			} else {
				setValues({ ...values, title: '', success: `Blog titled "${data.title}" is successfully updated` });
				if (isAuth() && isAuth().role === 1) {
					// Router.replace(`/admin/crud/${router.query.slug}`);
					Router.replace(`/admin`);
				} else if (isAuth() && isAuth().role === 0) {
					// Router.replace(`/user/crud/${router.query.slug}`);
					Router.replace(`/user`);
				}
			}
		});
	};

	const showError = () => (
		<div className="alert alert-danger" style={{ display: error ? '' : 'none' }}>
			{error}
		</div>
	);

	const showSuccess = () => (
		<div className="alert alert-success" style={{ display: success ? '' : 'none' }}>
			{success}
		</div>
    );

	const handleTagsChange = (e, { value }) => {
        formData.set('tags', e);
        setCheckedTag(e)
    };
    
    const handleCatChange = (e, { value }) => {
        formData.set('categories', e);
        setCheckedCat(e)
    };
    
    const handleImage = (file, callback) => {
        setValues({ ...values, initial: false });
		const reader = new FileReader();
		reader.onloadend = () => {
			callback(reader.result);
		};
		reader.readAsDataURL(file);
		formData.set('photo', file);
    };
    
    const thumbnailStyles = {
		width: 300,
		height: 200,
		marginTop: 0
    };
    
    const [ fileInfo, setFileInfo ] = React.useState(null);

	return (
		<React.Fragment>
			<Form fluid onSubmit={editBlog}>
				<FormGroup>
					<ControlLabel>
						<h6>Post Title</h6>
					</ControlLabel>
					<FormControl
						placeholder="Give an awesome title..."
						value={title}
						onChange={handleChange('title')}
					/>
					<HelpBlock>Required</HelpBlock>
				</FormGroup>
				<FormGroup>
					<ControlLabel>
						<h6>Post Body</h6>
					</ControlLabel>
					<ReactQuill
						modules={QuillModules}
						formats={QuillFormats}
						value={body}
						placeholder="Write something amazing"
						onChange={handleBody}
					/>
					<HelpBlock>Required</HelpBlock>
				</FormGroup>
                <FormGroup>
                    <FlexboxGrid>
                        <FlexboxGrid.Item colspan={11}>
                            <TagPicker
                                    block
                                    data={tags}
                                    value={checkedTag}
                                    onChange={handleTagsChange}
                                    placeholder="Select Tags"
                                />
							<HelpBlock>Required</HelpBlock>
                        </FlexboxGrid.Item>
                        <FlexboxGrid.Item colspan={2}></FlexboxGrid.Item>
                        <FlexboxGrid.Item colspan={11}>
                            <SelectPicker
                                block
                                data={categories}
                                value={checkedCat}
                                onChange={handleCatChange}
                                placeholder="Select Categories"
                            />
                            <HelpBlock>Required</HelpBlock>
                        </FlexboxGrid.Item>
                    </FlexboxGrid>
                    <FlexboxGrid>
						<FlexboxGrid.Item colspan={10}>
							<ControlLabel>
								<h6 className="pt-3">Select Thumbnail</h6>
							</ControlLabel>
							<Uploader
								fileListVisible={false}
								listType="picture"
								onUpload={(file) => {
									handleImage(file.blobFile, (value) => {
										setFileInfo(value);
									});
								}}
							>
								<button style={thumbnailStyles} type="button">
									{fileInfo ? (
										<img src={fileInfo} className="thumbnail-image" />
									) : (
										<Icon icon="image" size="5x" />
									)}
                                    {initial && (
										<img src={`${API}/blog/photo/${router.query.slug}`} className="thumbnail-image" />
									)}
								</button>
							</Uploader>
							<HelpBlock>Required</HelpBlock>
						</FlexboxGrid.Item>
					</FlexboxGrid>
                </FormGroup>
			</Form>
		</React.Fragment>
	);
};

export default withRouter(BlogUpdate);
