import { signout, isAuth } from '../actions/auth';
import Router from 'next/router';
import { useRouter } from 'next/router';
import Link from 'next/link'

import { Navbar, Nav, Button, Icon } from 'rsuite';

const Topnav = () => {
	return (
		<Navbar>
			<div className="container">
				<Navbar.Body>
					<Nav>
						<Nav.Item className="link-decoration-none" icon={<Icon icon="home" />}>
							<Link href="/">Home</Link>
						</Nav.Item>
						<Nav.Item className="link-decoration-none" icon={<Icon icon="book" />}>
							<Link href="/blogs">Blogs</Link>
						</Nav.Item>
						{!isAuth() && (
							<React.Fragment>
							<Nav.Item className="link-decoration-none" icon={<Icon icon="sign-in" />}>
								<Link href="/signin">Sign In</Link>
							</Nav.Item>
							<Nav.Item className="link-decoration-none" icon={<Icon icon="user-plus" />}>
								<Link href="/signup">Sign Up</Link>
							</Nav.Item>
							</React.Fragment>
						)}
						
						{isAuth() && (
							<Nav.Item className="link-decoration-none" icon={<Icon icon="sign-out" />} onClick={() => signout(() => Router.replace('/signin'))}>
							Sign Out
						</Nav.Item>
						)}
						
						
					</Nav>
					<Nav pullRight>
						<Nav.Item icon={<Icon icon="cog" />}>Settings</Nav.Item>
					</Nav>
				</Navbar.Body>
			</div>
		</Navbar>
	);
};

export default Topnav;
