import Link from 'next/link';
import renderHTML from 'react-render-html';
import moment from 'moment';
import { API } from '../../config';

import { Icon, TagGroup, Tag, Grid, Row, Col } from 'rsuite';

const Card = ({ blog }) => {
	const showBlogCategories = (blog) =>
		blog.categories.map((c, i) => (
			<Link key={i} href={`/categories/${c.slug}`}>
				<a className="small-category">{c.name}</a>
			</Link>
		));

	const showBlogTags = (blog) =>
		blog.tags.map((t, i) => (
			<Tag color="cyan" key={i}>
				<Link href={`/tags/${t.slug}`}>
					<a className="small-tag">{t.name}</a>
				</Link>
			</Tag>
		));

	return (
		<div className="article-box">
			<header>
                {showBlogCategories(blog)}
				<Link href={`/blogs/${blog.slug}`}>
					<a>
						<h6>{blog.title}</h6>
					</a>
				</Link>
			</header>
			<section>
				<p className="writer">
					<Icon icon="user" className="pr-2" /> {blog.postedBy.name} <Icon icon="calendar" className="px-2" />{' '}
					{moment(blog.updatedAt).fromNow()}
				</p>
			</section>

            <section>
                <Grid fluid>
                    <Row className="show-grid">
                        <Col xs={12}>
                            <div className="blog-excerpt">
                                {/* {renderHTML(blog.excerpt)} */}
                                <Link href={`/blogs/${blog.slug}`}>
                                    {renderHTML(blog.excerpt)}
                                </Link>
                            </div>
                        </Col>
                        <Col xs={12}>
                            <img
                                className="blog-thumbnail"
                                src={`${API}/blog/photo/${blog.slug}`}
                                alt={blog.title}
                            />
                        </Col>
                    </Row>
                </Grid>
            </section>

			{/* <div className="row">
				<div className="col-md-4">
					<section>
						<img
							className="img img-fluid"
							style={{ maxHeight: '150px', width: 'auto' }}
							src={`${API}/blog/photo/${blog.slug}`}
							alt={blog.title}
						/>
					</section>
				</div>
				<div className="col-md-8">
					<section>
						<div className="pb-3">{renderHTML(blog.excerpt)}</div>
						<Link href={`/blogs/${blog.slug}`}>
							<a className="btn btn-primary pt-2">Read more</a>
						</Link>
					</section>
				</div>
			</div> */}
			<section>
				<TagGroup>{showBlogTags(blog)}</TagGroup>
			</section>
		</div>
	);
};

export default Card;
