import { useState } from 'react';
import { useRouter } from 'next/router';
import { Menu, Segment, Icon, Input } from 'semantic-ui-react';
import { signout, isAuth } from '../actions/auth';

const Topnav = () => {
    const [activeItem, activateItem] = useState('home')

    const segmentStyle = {
        borderRadius: 0
    }

    const router = useRouter();

    return (
        <Segment inverted style={segmentStyle}>
            <Menu inverted pointing secondary>
                <Menu.Item>
                    <Icon name='grid layout'/>
                </Menu.Item>
                <Menu.Item
                    name='Home'
                    icon='home'
                    onClick={() => router.push('/')}
                />
                <Menu.Item
                    name='Sign In'
                    icon='sign-in'
                    onClick={() => router.push('/signin')}
                />
                <Menu.Item
                    name='Sign Up'
                    icon='add user'
                    onClick={() => router.push('/signup')}
                />
                {isAuth() && (
                    <Menu.Item
                    name='Sign Out'
                    icon='sign-out'
                    onClick={() => signout(() => router.push('/signin'))}
                />
                )}
                
                <Menu.Item position='right'>
                    <Input
                        action={{ type: 'submit', content: 'Search' }}
                        placeholder='Type to search...'
                    />
                </Menu.Item>
            </Menu>
        </Segment>
    )
}

export default Topnav;
