import Link from 'next/link';
import renderHTML from 'react-render-html';
import moment from 'moment';
import { API } from '../../config';

import { Icon, TagGroup, Tag } from 'rsuite';

const Card = ({ blog }) => {
	const showBlogCategories = (blog) =>
		blog.categories.map((c, i) => (
			<Link key={i} href={`/categories/${c.slug}`}>
				<a className="btn btn-primary mr-1 ml-1 mt-3">{c.name}</a>
			</Link>
		));

	const showBlogTags = (blog) =>
		blog.tags.map((t, i) => (
            <Tag color="cyan" key={i}>
                <Link href={`/tags/${t.slug}`}>
                    <a className="link-decoration-none">{t.name}</a>
                </Link>
            </Tag>
		));

	return (
		<div className="article-box">
			<header>
				<Link href={`/blogs/${blog.slug}`}>
					<a>
						<h6>{blog.title}</h6>
					</a>
				</Link>
			</header>
			<section>
				<p className="writer">
					<Icon icon="user"className="pr-2" /> {blog.postedBy.name} <Icon icon="calendar" className="px-2" /> {moment(blog.updatedAt).fromNow()}
				</p>
			</section>
			<section>
                <TagGroup>
                    {showBlogTags(blog)}
                </TagGroup>

				{showBlogCategories(blog)}
				<br />
				<br />
			</section>

			<div className="row">
				<div className="col-md-4">
					<section>
						<img
							className="img img-fluid"
							style={{ maxHeight: '150px', width: 'auto' }}
							src={`${API}/blog/photo/${blog.slug}`}
							alt={blog.title}
						/>
					</section>
				</div>
				<div className="col-md-8">
					<section>
						<div className="pb-3">{renderHTML(blog.excerpt)}</div>
						<Link href={`/blogs/${blog.slug}`}>
							<a className="btn btn-primary pt-2">Read more</a>
						</Link>
					</section>
				</div>
			</div>
		</div>
	);
};

export default Card;
