import { useState, useEffect } from 'react';
import { getCookie } from '../../actions/auth';
import { create, getCategories, removeCategory } from '../../actions/category';
import { Form, Button, Icon, List, Message } from 'semantic-ui-react';

const Category = () => {
	const [ values, setValues ] = useState({
		name: '',
		error: false,
		success: false,
		categories: [],
		removed: false,
		reload: false,
		content: 'Click Add to add a category. Click a category to remove it'
	});

	const { name, error, success, categories, removed, reload, content } = values;
	const token = getCookie('token');

	useEffect(
		() => {
			loadCategories();
		},
		[ reload ]
	);

	const loadCategories = () => {
		getCategories().then((data) => {
			if (data.error) {
				console.log(data.error);
			} else {
				setValues({ ...values, categories: data });
			}
		});
	};

	const showCategories = () => {
		return categories.map((c, i) => {
			return (
				<List.Item>
					<List.Content>
						<Button icon labelPosition='right' style={{width: 200}} onClick={() => deleteConfirm(c.slug)}>
							<Icon name='trash alternate' />
							{c.name}
						</Button>
					</List.Content>
				</List.Item>
			);
		});
	};

	const deleteConfirm = (slug) => {
		let answer = window.confirm('Are you sure you want to delete this category?');
		if (answer) {
			deleteCategory(slug);
		}
	};

	const deleteCategory = (slug) => {
		removeCategory(slug, token).then((data) => {
			if (data.error) {
				console.log(data.error);
			} else {
				setValues({ ...values, error: false, success: false, name: '', removed: !removed, reload: !reload });
			}
		});
	};

	const clickSubmit = (e) => {
		e.preventDefault();
		create({ name }, token).then((data) => {
			if (data.error) {
				setValues({ ...values, error: data.error, success: false });
			} else {
				setValues({ ...values, error: false, success: true, name: '', removed: false, reload: !reload });
			}
		});
	};

	const handleChange = (e) => {
		setValues({ ...values, name: e.target.value, error: false, success: false, removed: '' });
	};

	const showSuccess = () => {
		if (success) {
			return <p className="text-success">Category is created</p>;
		}
	};

	const showError = () => {
		if (error) {
			return <p className="text-danger">Category already exist</p>;
		}
	};

	const showRemoved = () => {
		if (removed) {
			return <p className="text-danger">Category is removed</p>;
		}
	};

	const mouseMoveHandler = (e) => {
		setValues({ ...values, error: false, success: false, removed: '' });
	};

	const newCategoryFom = () => (
		<div>
			<Form onSubmit={clickSubmit}>
				<Form.Group>
					<Form.Input placeholder="Add a new category" value={name} onChange={handleChange} />
					<Form.Button content="Add" icon="plus" type="submit" />
				</Form.Group>
			</Form>
		</div>
	);

	return (
		<React.Fragment>
			{showSuccess()}
			{showError()}
			{showRemoved()}
			<div onMouseMove={mouseMoveHandler}>
				{newCategoryFom()}
				<Message
					header="Category Form"
					content={content}
				/>
				<List verticalAlign='middle'>
					{showCategories()}
				</List>
			</div>
		</React.Fragment>
	);
};

export default Category;
