import Layout from '../../components/Layout';
import Admin from '../../components/auth/Admin';
import { Grid, Button, Icon } from 'semantic-ui-react';
import Router from 'next/router';

const AdminIndex = () => {
	return (
		<Layout>
			<Admin>
				<Grid>
					<h2>Admin Dashboard</h2>
					<Grid.Row>
						<Grid.Column width={6}>
							<Button onClick={() => Router.push('/admin/crud/category-tag')}>
								<Button.Content visible>Manage Category & Tags</Button.Content>
							</Button>
							<Button onClick={() => Router.push('/admin/crud/blog')}>
								<Button.Content visible>Create Blog</Button.Content>
							</Button>
						</Grid.Column>
						<Grid.Column width={6} />
					</Grid.Row>
				</Grid>
			</Admin>
		</Layout>
	);
};

export default AdminIndex;
