import Link from 'next/link';
import { useState, useEffect } from 'react';
import Router from 'next/router';
import { getCookie, isAuth, updateUser } from '../../actions/auth';
import { getProfile, update } from '../../actions/user';
import { API } from '../../config';
import { imageupload } from '../../actions/imgur';

import { Form, FormGroup, FormControl, ControlLabel, HelpBlock, Grid, Row, Col, SelectPicker, Button } from 'rsuite';

const ProfileUpdate = () => {
	const [ values, setValues ] = useState({
		username: '',
		name: '',
		email: '',
		avatar: '',
		cover: '',
		about: '',
		blood: '',
		contact: '',
		address: '',
		password: '',
		error: false,
		success: false,
		loading: false,
		photo: '',
		userData: ''
	});

	const token = getCookie('token');
	const {
		username,
		name,
		email,
		avatar,
		cover,
		about,
		blood,
		contact,
		address,
		password,
		error,
		success,
		loading,
		photo,
		userData
	} = values;

	const init = () => {
		getProfile(token).then((data) => {
			if (data.error) {
				setValues({ ...values, error: data.error });
			} else {
				setValues({
					...values,
					username: data.username,
					name: data.name,
					email: data.email,
					avatar: data.avatar,
					cover: data.cover,
					about: data.about,
					blood: data.blood,
					contact: data.contact,
					address: data.address
				});
			}
		});
	};

	useEffect(() => {
		init();
	}, []);

	const [ bloodGroups, setBloodGroups ] = useState([
		{ label: 'A+', value: 'A+' },
		{ label: 'A-', value: 'A-' },
		{ label: 'B+', value: 'B+' },
		{ label: 'B-', value: 'B-' },
		{ label: 'AB+', value: 'AB+' },
		{ label: 'AB-', value: 'AB-' },
		{ label: 'O+', value: 'O+' },
		{ label: 'O-', value: 'O-' }
	]);

	const handleChange = (name) => (e) => {
		const value = name === 'photo' ? e.target.files[0] : e;
		let userFormData = new FormData();
		userFormData.set(name, value);
		setValues({ ...values, [name]: value, userData: userFormData, error: false, success: false });
	};

	const handleBloodChange = (e, { value }) => {
		userFormData.set('blood', e);
		setBloodGroups(e);
	};

	const handleSubmit = (e) => {
		e.preventDefault();
		setValues({ ...values, loading: true });
		update(token, userData).then((data) => {
			if (data.error) {
				setValues({ ...values, error: data.error, success: false, loading: false });
			} else {
				updateUser(data, () => {
					setValues({
						...values,
						username: data.username,
						name: data.name,
						email: data.email,
						avatar: data.avatar,
						cover: data.cover,
						about: data.about,
						password: '',
						blood: data.blood,
						contact: data.contact,
						address: data.address,
						success: true,
						loading: false
					});
				});
			}
		});
	};

	// const profileUpdateForm = () => (
	//     <form onSubmit={handleSubmit}>
	//         <div className="form-group">
	//             <label className="btn btn-outline-info">
	//                 Profile photo
	//                 <input onChange={handleChange('photo')} type="file" accept="image/*" hidden />
	//             </label>
	//         </div>
	//         <div className="form-group">
	//             <label className="text-muted">Username</label>
	//             <input onChange={handleChange('username')} type="text" value={username} className="form-control" />
	//         </div>
	//         <div className="form-group">
	//             <label className="text-muted">Name</label>
	//             <input onChange={handleChange('name')} type="text" value={name} className="form-control" />
	//         </div>
	//         <div className="form-group">
	//             <label className="text-muted">Email</label>
	//             <input onChange={handleChange('email')} type="text" value={email} className="form-control" />
	//         </div>
	//         <div className="form-group">
	//             <label className="text-muted">About</label>
	//             <textarea onChange={handleChange('about')} type="text" value={about} className="form-control" />
	//         </div>
	//         <div className="form-group">
	//             <label className="text-muted">Password</label>
	//             <input onChange={handleChange('password')} type="password" value={password} className="form-control" />
	//         </div>
	//         <div>
	//             <button type="submit" className="btn btn-primary">
	//                 Submit
	//             </button>
	//         </div>
	//     </form>
	// );

	// const showError = () => (
	//     <div className="alert alert-danger" style={{ display: error ? '' : 'none' }}>
	//         {error}
	//     </div>
	// );

	// const showSuccess = () => (
	//     <div className="alert alert-success" style={{ display: success ? '' : 'none' }}>
	//         Profile updated
	//     </div>
	// );

	// const showLoading = () => (
	//     <div className="alert alert-info" style={{ display: loading ? '' : 'none' }}>
	//         Loading...
	//     </div>
	// );

	return (
		<React.Fragment>
			<Grid fluid>
				<Row className="show-grid">
					<Col xs={24}>
						<Form fluid onSubmit={handleSubmit}>
							<FormGroup>
								<ControlLabel>Username</ControlLabel>
								<FormControl
									value={username}
									onChange={handleChange('username')}
								/>
							</FormGroup>
							<FormGroup>
								<ControlLabel>Full Name</ControlLabel>
								<FormControl
									value={name}
									onChange={handleChange('name')}
								/>
							</FormGroup>
							<FormGroup>
								<ControlLabel>Email</ControlLabel>
								<FormControl
									value={email}
									onChange={handleChange('email')}
								/>
							</FormGroup>
							<FormGroup>
								<ControlLabel>About</ControlLabel>
								<FormControl
									placeholder="Write about yourself"
									componentClass="textarea"
									rows={5}
									value={about}
									onChange={handleChange('about')}
								/>
							</FormGroup>
							<FormGroup>
								<SelectPicker
									block
									data={bloodGroups}
									value={blood}
									onChange={handleBloodChange}
									placeholder="Select Blood Group"
								/>
							</FormGroup>
                            <FormGroup>
								<ControlLabel>Contact No</ControlLabel>
								<FormControl
									value={contact}
									onChange={handleChange('contact')}
								/>
							</FormGroup>
                            <FormGroup>
								<ControlLabel>Address</ControlLabel>
								<FormControl
									value={address}
									onChange={handleChange('address')}
								/>
							</FormGroup>
                            <Button appearance="primary" block type="submit">
								Submit
							</Button>
						</Form>
					</Col>
				</Row>
			</Grid>
			{/* <div className="container">
                <div className="row">
                    <div className="col-md-4">
                        <img
                            src={`${API}/user/photo/${username}`}
                            className="img img-fluid img-thumbnail mb-3"
                            style={{ maxHeight: 'auto', maxWidth: '100%' }}
                            alt="user profile"
                        />
                    </div>
                    <div className="col-md-8 mb-5">
                        {showSuccess()}
                        {showError()}
                        {showLoading()}
                        {profileUpdateForm()}
                    </div>
                </div>
            </div> */}
		</React.Fragment>
	);
};

export default ProfileUpdate;
