import { Button, Form, Icon, Message, Grid, Header, Modal } from 'semantic-ui-react';
import { useState } from 'react';
import { signup } from '../../actions/auth';

const SignupComponent = () => {
	const [ values, setValues ] = useState({
		name: '',
		email: '',
		password: '',
		error: '',
		checking: false,
		message: '',
		showForm: true,
		ticked: false
	});

	const { name, email, password, error, checking, message, showForm, ticked } = values;

	const headerStyle = {
		textAlign: 'center'
	};
	const subHeaderStyle = {
		paddingLeft: 30,
		paddingRight: 30,
		paddingTop: 15,
		color: 'grey'
	};

	const handleSubmit = (e) => {
		e.preventDefault();
		setValues({ ...values, checking: true, error: false });
		const user = { name, email, password };

		signup(user).then((data) => {
			if (data.error) {
				setValues({ ...values, error: data.error });
			} else {
				setValues({
					...values,
					name: '',
					email: '',
					password: '',
					checking: false,
					error: false,
					message: data.message,
					showForm: false
				});
			}
		});
	};

	const handleChange = (name) => (e) => {
		setValues({ ...values, error: false, [name]: e.target.value });
	};

	const handleTicked = (e) => {
		setValues({ ...values, ticked: e.target.checked });
	};

	const handleClose = () => {
		setValues({ ...values, error: '', message: '' });
	};

	return (
		<React.Fragment>
			<Grid>
				<Grid.Row>
					<Grid.Column width="8" verticalAlign="middle">
						<div style={headerStyle}>
							<Header as="h2" icon>
								<Icon name="home" />
								Comics Ghor
								<Header.Subheader>A Canvas to Paint Your Mind Out</Header.Subheader>
								<Header.Subheader style={subHeaderStyle}>
									Welcome to Comics Ghor, a Bangladeshi website. The purpose of Comics Ghor is to
									create a free platform for all the Bangladeshi people, of all age, who desire to
									draw comics or write novels, stories or more.
								</Header.Subheader>
							</Header>
						</div>
					</Grid.Column>
					<Grid.Column width="8" style={{ padding: '35px' }}>
						<div>
							<Message
								attached
								header="Welcome to our site!"
								content="Fill out the form below to sign-up for a new account"
							/>
							<Form className="attached fluid segment" onSubmit={handleSubmit} loading={checking}>
								<Form.Input
									value={name}
									onChange={handleChange('name')}
									label="Username"
									placeholder="Username"
									type="text"
								/>
								<Form.Input
									value={email}
									onChange={handleChange('email')}
									label="Email"
									placeholder="Email"
									type="email"
								/>
								<Form.Input
									value={password}
									onChange={handleChange('password')}
									label="Password"
									type="password"
								/>
								<Form.Checkbox
									inline
									label="I agree to the terms and conditions"
									checked={ticked}
									onChange={handleTicked}
								/>
								<Button color="blue" type="submit" disabled={ticked ? '' : 'true'}>
									Sign Up
								</Button>
							</Form>
							<Message attached="bottom" warning>
								<Icon name="help" />
								Already signed up?&nbsp;<a href="#">Signin here</a>&nbsp;instead.
							</Message>
						</div>
					</Grid.Column>
				</Grid.Row>
			</Grid>
			<Modal open={error} onClose={handleClose} basic size="tiny">
				<Header icon="browser" content="Signup Error" />
				<Modal.Content>{error}</Modal.Content>
				<Modal.Actions>
					<Button color="green" onClick={handleClose} inverted>
						<Icon name="checkmark" /> Got it
					</Button>
				</Modal.Actions>
			</Modal>
			<Modal open={message} onClose={handleClose} basic size="tiny">
				<Header icon="browser" content="Signup Successful" />
				<Modal.Content>{message}</Modal.Content>
				<Modal.Actions>
					<Button color="green" onClick={handleClose} inverted>
						<Icon name="checkmark" /> Got it
					</Button>
				</Modal.Actions>
			</Modal>
		</React.Fragment>
	);
};

export default SignupComponent;
