import Head from 'next/head';
import Link from 'next/link';
import Layout from '../../components/Layout';
import { useState, useEffect, useRef } from 'react';
import { singleBlog, listRelated } from '../../actions/blog';
import { API, DOMAIN, APP_NAME, FB_APP_ID } from '../../config';
import renderHTML from 'react-render-html';
import moment from 'moment';
import SmallCard from '../../components/blog/SmallCard';
import Img from 'react-image';

import { Grid, Row, Col, Loader, Icon, TagGroup, Tag } from 'rsuite';

const SingleBlog = ({ blog, query }) => {
	const [ related, setRelated ] = useState([]);

	const loadRelated = () => {
		listRelated({ blog }).then((data) => {
			if (data.error) {
				console.log(data.error);
			} else {
				setRelated(data);
			}
		});
	};

	const IMAGES = useREf();
	const loadImages = (blog) => {
		blog.image[0].split(',').forEach((image, i) => {
			const temp = {};
			temp.thumbnail = `https://i.imgur.com/${imgur}s.png`;
			temp.src = `https://i.imgur.com/${imgur}h.png`;
			(temp.thumbnailWidth = 320), (temp.thumbnailHeight = 175);
		});
	};

	useEffect(() => {
        loadRelated();
        loadImages()
	}, []);

	const head = () => (
		<Head>
			<title>
				{blog.title} | {APP_NAME}
			</title>
			<meta name="description" content={blog.mdesc} />
			<link rel="canonical" href={`${DOMAIN}/blogs/${query.slug}`} />
			<meta property="og:title" content={`${blog.title}| ${APP_NAME}`} />
			<meta property="og:description" content={blog.mdesc} />
			<meta property="og:type" content="webiste" />
			<meta property="og:url" content={`${DOMAIN}/blogs/${query.slug}`} />
			<meta property="og:site_name" content={`${APP_NAME}`} />

			<meta property="og:image" content={`${API}/blog/photo/${blog.slug}`} />
			<meta property="og:image:secure_url" ccontent={`${API}/blog/photo/${blog.slug}`} />
			<meta property="og:image:type" content="image/jpg" />
			<meta property="fb:app_id" content={`${FB_APP_ID}`} />
		</Head>
	);

	const showBlogCategories = (blog) =>
		blog.categories.map((c, i) => (
			<Link key={i} href={`/categories/${c.slug}`}>
				<a className="blog-category">{c.name}</a>
			</Link>
		));

	const showBlogTags = (blog) =>
		blog.tags.map((t, i) => (
			<Tag style={{ backgroundColor: '#ccc' }} key={i}>
				<Link href={`/tags/${t.slug}`}>
					<a className="small-tag">{t.name}</a>
				</Link>
			</Tag>
		));

	const showRelatedBlog = () => {
		return related.map((blog, i) => (
			<div className="col-md-4" key={i}>
				<article>
					<SmallCard blog={blog} />
				</article>
			</div>
		));
	};

	return (
		<React.Fragment>
			{head()}
			<Layout>
				<main>
					<article>
						<div className="container">
							<Grid fluid>
								<Row className="show-grid">
									<Col xs={24}>
										<h3 className="text-center">{blog.title}</h3>
										<div className="text-center">
											<Img
												className="blog-main-image"
												src={`${API}/blog/photo/${blog.slug}`}
												loader={<Loader size="lg" />}
											/>
										</div>
										<div className="text-center my-2 grey-text">
											<Icon icon="user" className="pr-2" /> {blog.postedBy.name}{' '}
											<Icon icon="calendar" className="px-2" /> {moment(blog.updatedAt).fromNow()}
											{showBlogCategories(blog)}
										</div>
									</Col>
									<Col xs={24}>
										<div className="px-5 py-2">{renderHTML(blog.body)}</div>
									</Col>
									<Col xs={24}>
										<div className="text-center">
											{blog.image[0]
												.split(',')
												.map((imgur, i) => (
													<Img
														className="blog-imgur-image"
														src={`https://i.imgur.com/${imgur}h.png`}
														loader={<Loader size="lg" />}
													/>
												))}
										</div>
									</Col>
									<Col xs={24}>
										<div className="px-5 py-2 tag-list my-2">
											<TagGroup>{showBlogTags(blog)}</TagGroup>
										</div>
									</Col>
								</Row>
							</Grid>
						</div>
						{/* <div className="container-fluid">
                            <section>
                                <div className="row" style={{ marginTop: '-30px' }}>
                                    <img
                                        src={`${API}/blog/photo/${blog.slug}`}
                                        alt={blog.title}
                                        className="img img-fluid featured-image"
                                    />
                                </div>
                            </section>

                            <section>
                                <div className="container">
                                    <h1 className="display-2 pb-3 pt-3 text-center font-weight-bold">{blog.title}</h1>
                                    <p className="lead mt-3 mark">
                                        Written by {blog.postedBy.name} | Published {moment(blog.updatedAt).fromNow()}
                                    </p>

                                    <div className="pb-3">
                                        {showBlogCategories(blog)}
                                        {showBlogTags(blog)}
                                        <br />
                                        <br />
                                    </div>
                                </div>
                            </section>
                        </div>

                        <div className="container">
                            <section>
                                <div className="col-md-12 lead">{renderHTML(blog.body)}</div>
                            </section>
                        </div>

                        <div className="container">
                            <h4 className="text-center pt-5 pb-5 h2">Related blogs</h4>
                            <div className="row">{showRelatedBlog()}</div>
                        </div>

                        <div className="container pb-5">
                            <p>show comments</p>
                        </div> */}
					</article>
				</main>
			</Layout>
		</React.Fragment>
	);
};

SingleBlog.getInitialProps = ({ query }) => {
	return singleBlog(query.slug).then((data) => {
		if (data.error) {
			console.log(data.error);
		} else {
			// console.log('GET INITIAL PROPS IN SINGLE BLOG', data);
			return { blog: data, query };
		}
	});
};

export default SingleBlog;
