import { signout, isAuth } from '../actions/auth';
import Router from 'next/router';
import Link from 'next/link';

import { Navbar, Nav, Button, Icon } from 'rsuite';

const Topnav = () => {
	return (
		<Navbar>
			<div className="container">
				<Navbar.Body>
					<Nav>
						<Nav.Item className="link-decoration-none" icon={<Icon icon="home" />}>
							<Link href="/"><a>Home</a></Link>
						</Nav.Item>
						<Nav.Item className="link-decoration-none" icon={<Icon icon="book" />}>
							<Link href="/blogs"><a>Posts</a></Link>
						</Nav.Item>
						<Nav.Item className="link-decoration-none" icon={<Icon icon="search" />}>
							<Link href="/blogs/search"><a>Search</a></Link>
						</Nav.Item>
						{!isAuth() && (
							<React.Fragment>
								<Nav.Item className="link-decoration-none" icon={<Icon icon="sign-in" />}>
									<Link href="/signin"><a>Sign In</a></Link>
								</Nav.Item>
								<Nav.Item className="link-decoration-none" icon={<Icon icon="user-plus" />}>
									<Link href="/signup"><a>Sign Up</a></Link>
								</Nav.Item>
							</React.Fragment>
						)}

						{isAuth() && (
							<Nav.Item
								className="link-decoration-none"
								icon={<Icon icon="sign-out" />}
								onClick={() => signout(() => Router.replace('/signin'))}
							>
								Sign Out
							</Nav.Item>
						)}
					</Nav>
					<Nav pullRight>
						<React.Fragment>
						{isAuth() && isAuth().role === 1 && (
							<Nav.Item 
								className="link-decoration-none"
								icon={<Icon icon="user-secret" />}
							>
								<Link href="/admin"><a>{`${isAuth().name}`}</a></Link>
							</Nav.Item>
						)}
						{isAuth() && isAuth().role === 0 && (
							<Nav.Item
								className="link-decoration-none"
								icon={<Icon icon="user" />}
							>
								<Link href="/user"><a>{`${isAuth().name}`}</a></Link>
							</Nav.Item>
						)}
						</React.Fragment>
					</Nav>
				</Navbar.Body>
			</div>
		</Navbar>
	);
};

export default Topnav;
