import { useState, useEffect } from 'react';
import { signup, isAuth } from '../../actions/auth';
import Router from 'next/router';
import Link from 'next/link';

import {
	Form,
	FormGroup,
	FormControl,
	ControlLabel,
	HelpBlock,
	Icon,
	Button,
	ButtonToolbar,
	Grid,
	Row,
	Col,
	Modal,
	Loader
} from 'rsuite';

const SignupComponent = () => {
	const [ values, setValues ] = useState({
		name: '',
		email: '',
		password: '',
		error: false,
		checking: false,
		message: '',
		showForm: true
	});

	const { name, email, password, error, checking, message } = values;

	const headerStyle = {
		textAlign: 'center'
	};
	const subHeaderStyle = {
		paddingLeft: 30,
		paddingRight: 30,
		paddingTop: 15,
		color: 'grey'
	};

	useEffect(() => {
		isAuth() && Router.replace('/');
	}, []);

	const handleSubmit = (e) => {
		e.preventDefault();
		setValues({ ...values, checking: true, error: false });
		const user = { name, email, password };

		signup(user).then((data) => {
			if (data.error) {
				setValues({ ...values, error: data.error });
			} else {
				setValues({
					...values,
					name: '',
					email: '',
					password: '',
					checking: false,
					error: false,
					message: data.message
				});
			}
		});
	};

	const handleChange = (name) => (e) => {
		setValues({ ...values, error: false, [name]: e });
	};

	const handleClose = () => {
		setValues({ ...values, error: '' });
	};

	const pushSignin = () => {
		setValues({ ...values, message: '' });
		Router.replace('/signin');
	};

	return (
		<React.Fragment>
			<Grid fluid>
				{checking && <Loader backdrop content="loading..." vertical size="lg" />}
				<Row className={`show-grid ${checking ? 'modal-back' : ''}`}>
					<Col xs={12} />
					<Col xs={12}>
						<div className="text-center">
							<img style={{ maxWidth: '100px' }} src="/images/comicsghor.png" />
						</div>
						<div className="py-3">
							<h4 className="text-center">Welcome to Comics Ghor</h4>
							<h6 className="text-center">Fill out the form below to sign up for a new account</h6>
						</div>
						<div>
							<Form fluid onSubmit={handleSubmit}>
								<FormGroup>
									<ControlLabel>Username</ControlLabel>
									<FormControl
										placeholder="Username"
										name="name"
										type="name"
										value={name}
										onChange={handleChange('name')}
										autoComplete="off"
									/>
									<HelpBlock>
										{name.length !== 0 ? (
											<span>
												<Icon icon="check" style={{ color: 'green' }} /> Username seems valid
											</span>
										) : (
											<span>
												<Icon icon="close" style={{ color: 'red' }} /> Provide a valid username
											</span>
										)}
									</HelpBlock>
								</FormGroup>
								<FormGroup>
									<ControlLabel>Email</ControlLabel>
									<FormControl
										placeholder="Email"
										name="email"
										type="email"
										value={email}
										onChange={handleChange('email')}
										autoComplete="off"
									/>
									<HelpBlock>
										{/\S+@\S+\.\S+/.test(email) ? (
											<span>
												<Icon icon="check" style={{ color: 'green' }} /> Email address seems
												valid
											</span>
										) : (
											<span>
												<Icon icon="close" style={{ color: 'red' }} /> Provide a valid email
												address
											</span>
										)}
									</HelpBlock>
								</FormGroup>
								<FormGroup>
									<ControlLabel>Password</ControlLabel>
									<FormControl
										placeholder="Password"
										name="password"
										type="password"
										value={password}
										onChange={handleChange('password')}
									/>
									<HelpBlock>
										{password.length < 6 ? (
											<span>
												<Icon icon="close" style={{ color: 'red' }} /> Weak password
											</span>
										) : (
											<span>
												<Icon icon="check" style={{ color: 'green' }} /> Password seems valid
											</span>
										)}
									</HelpBlock>
								</FormGroup>
								<FormGroup>
									<ButtonToolbar>
										<Button appearance="primary" type="submit">
											Submit
										</Button>
									</ButtonToolbar>
								</FormGroup>
							</Form>
							<h6 className="text-center py-2">
								Don't have an account? <Link href="/signin">Sign In</Link>
							</h6>
						</div>
					</Col>
				</Row>
			</Grid>
			<Modal backdrop="static" show={error.length > 0 ? true : false} onHide={handleClose}>
				<Modal.Header>
					<Modal.Title>
						<Icon icon="exclamation-circle" style={{ color: '#ffc845' }} size="lg" className="pr-2" />
						Sign In Error
					</Modal.Title>
				</Modal.Header>
				<Modal.Body>{error}</Modal.Body>
				<Modal.Footer>
					<Button onClick={handleClose} appearance="primary">
						OK, Got it
					</Button>
				</Modal.Footer>
			</Modal>
			<Modal backdrop="static" show={message.length > 0 ? true : false} onHide={pushSignin}>
				<Modal.Header>
					<Modal.Title>
						<Icon icon="check" style={{ color: '#00a78e' }} size="lg" className="pr-2" />
						Sign In Successful
					</Modal.Title>
				</Modal.Header>
				<Modal.Body>
					{message}
				</Modal.Body>
				<Modal.Footer>
					<Button onClick={pushSignin} appearance="primary">
						OK, Got it
					</Button>
				</Modal.Footer>
			</Modal>
		</React.Fragment>
	);
};

export default SignupComponent;
