import { signout, isAuth } from '../actions/auth';
import Router from 'next/router';
import Link from 'next/link';

import { Navbar, Nav, Button, Icon } from 'rsuite';

const Topnav = () => {
	return (
		<Navbar>
			<div className="container">
				<Navbar.Body>
					<Nav>
						<Nav.Item
							className="link-decoration-none"
							icon={<Icon icon="home" />}
							onClick={() => {
								Router.replace('/');
							}}
						>
							Home
						</Nav.Item>
						<Nav.Item
							className="link-decoration-none"
							icon={<Icon icon="book" />}
							onClick={() => {
								Router.replace('/blogs');
							}}
						>
							Posts
						</Nav.Item>
						{isAuth() && (
							<React.Fragment>
								<Nav.Item
									className="link-decoration-none"
									icon={<Icon icon="pencil" />}
									onClick={() => {
										Router.replace('/user/crud/blog');
									}}
								>
									Write
								</Nav.Item>
								<Nav.Item
									className="link-decoration-none"
									icon={<Icon icon="search" />}
									onClick={() => {
										Router.replace('/blogs/search');
									}}
								>
									Search
								</Nav.Item>
								<Nav.Item
									className="link-decoration-none"
									icon={<Icon icon="sign-out" />}
									onClick={() => signout(() => Router.replace('/signin'))}
								>
									Sign Out
								</Nav.Item>
							</React.Fragment>
						)}
						{!isAuth() && (
							<React.Fragment>
								<Nav.Item
									className="link-decoration-none"
									icon={<Icon icon="sign-in" />}
									onClick={() => {
										Router.replace('/signin');
									}}
								>
									Sign In
								</Nav.Item>
								<Nav.Item
									className="link-decoration-none"
									icon={<Icon icon="user-plus" />}
									onClick={() => {
										Router.replace('/signup');
									}}
								>
									Sign Up
								</Nav.Item>
							</React.Fragment>
						)}
					</Nav>
					<Nav pullRight>
						<React.Fragment>
							{isAuth() &&
							isAuth().role === 1 && (
								<Nav.Item
									className="link-decoration-none"
									icon={<Icon icon="user-secret" />}
									onClick={() => {
										Router.replace('/admin');
									}}
								>
									{`${isAuth().name}`}
								</Nav.Item>
							)}
							{isAuth() &&
							isAuth().role === 0 && (
								<Nav.Item
									className="link-decoration-none"
									icon={<Icon icon="user" />}
									onClick={() => {
										Router.replace('/user');
									}}
								>
									{`${isAuth().name}`}
								</Nav.Item>
							)}
						</React.Fragment>
					</Nav>
				</Navbar.Body>
			</div>
		</Navbar>
	);
};

export default Topnav;
