import { Menu, Segment, Icon, Input, Container } from 'semantic-ui-react';
import { signout, isAuth } from '../actions/auth';
import Router from 'next/router';
import { useRouter } from 'next/router';

const Topnav = () => {

    const segmentStyle = {
        borderRadius: 0,
        marginTop: 0,
        padding: 5
    }

    const router = useRouter()

    return (
        <Segment inverted style={segmentStyle} color="blue" >
            <Container>
                <Menu inverted secondary color="blue">
                    <Menu.Item>
                        <Icon name='grid layout' />
                    </Menu.Item>
                    <Menu.Item
                        name='Home'
                        icon='home'
                        active={router.pathname === "/" ? true : false}
                        onClick={() => Router.replace('/')}
                    />

                    <Menu.Menu position='right'>
                        <Menu.Item>
                            <Input
                                action={{ type: 'submit', icon: 'search' }}
                                placeholder='Search...'
                            />
                        </Menu.Item>
                        {!isAuth() && (
                            <>
                                <Menu.Item
                                    name='Sign In'
                                    icon='sign-in'
                                    active={router.pathname === "/signin" ? true : false}
                                    onClick={() => Router.replace('/signin')}
                                />
                                <Menu.Item
                                    name='Sign Up'
                                    icon='add user'
                                    active={router.pathname === "/signup" ? true : false}
                                    onClick={() => Router.replace('/signup')}
                                />
                            </>
                        )}
                        {isAuth() && isAuth().role === 0 && (
                            <Menu.Item
                                name={`${isAuth().name}`}
                                icon='user circle'
                                active={router.pathname === "/user" ? true : false}
                                onClick={() => Router.replace('/user')}
                            />
                        )}
                        {isAuth() && isAuth().role === 1 && (
                            <Menu.Item
                                name={`${isAuth().name}`}
                                icon='user circle'
                                active={router.pathname === "/admin" ? true : false}
                                onClick={() => Router.replace('/admin')}
                            />
                        )}
                        {isAuth() && (
                            <Menu.Item
                                name='Sign Out'
                                icon='sign-out'
                                onClick={() => signout(() => Router.replace('/signin'))}
                            />
                        )}
                    </Menu.Menu>
                </Menu>
            </Container>
        </Segment>
    )
}

export default Topnav;
