import Link from 'next/link';
import { useState, useEffect, useRef } from 'react';
import Router from 'next/router';
import dynamic from 'next/dynamic';
import { withRouter } from 'next/router';
import { getCookie, isAuth } from '../../actions/auth';
import { getCategories } from '../../actions/category';
import { imageupload } from '../../actions/imgur';
import { getTags } from '../../actions/tag';
import { createBlog } from '../../actions/blog';
const ReactQuill = dynamic(() => import('react-quill'), { ssr: false });
import '../../node_modules/react-quill/dist/quill.snow.css';
import { QuillModules, QuillFormats } from '../../helpers/quill';

import {
	Form,
	FormGroup,
	FormControl,
	ControlLabel,
	HelpBlock,
	TagPicker,
	FlexboxGrid,
	SelectPicker,
	Uploader,
	Icon,
	Loader,
	Button,
	ButtonToolbar
} from 'rsuite';

const CreateBlog = ({ router }) => {
	const blogFromLS = () => {
		if (typeof window === 'undefined') {
			return false;
		}

		if (localStorage.getItem('blog')) {
			return JSON.parse(localStorage.getItem('blog'));
		} else {
			return false;
		}
	};

	const [ categories, setCategories ] = useState([]);
	const [ tags, setTags ] = useState([]);

	const [ body, setBody ] = useState(blogFromLS());
	const [ values, setValues ] = useState({
		error: '',
		sizeError: '',
		success: '',
		formData: '',
		title: '',
		images: [],
		uploading: false
	});

	const { error, sizeError, success, formData, title, images, uploading } = values;
	const token = getCookie('token');

	useEffect(
		() => {
			setValues({ ...values, formData: new FormData() });
			initCategories();
			initTags();
		},
		[ router ]
	);

	const initCategories = () => {
		getCategories().then((data) => {
			if (data.error) {
				setValues({ ...values, error: data.error });
			} else {
				const items = [];
				data.forEach((item, i) => {
					items[i] = {
						key: i,
						value: item._id,
						label: item.name
					};
				});
				setCategories(items);
			}
		});
	};

	const initTags = () => {
		getTags().then((data) => {
			if (data.error) {
				setValues({ ...values, error: data.error });
			} else {
				const items = [];
				data.forEach((item, i) => {
					items[i] = {
						key: i,
						value: item._id,
						label: item.name
					};
				});
				setTags(items);
			}
		});
	};

	const publishBlog = (e) => {
		e.preventDefault();
		setValues({ ...values, uploading: true });
		createBlog(formData, token).then((data) => {
			if (data.error) {
				setValues({ ...values, error: data.error });
			} else {
				setValues({
					...values,
					uploading: false,
					title: '',
					error: '',
					success: `A new blog titled "${data.title}" is created`
				});
				setBody('');
				setCategories([]);
				setTags([]);
				setThumbnail('');
			}
		});
	};

	const [ thumbnail, setThumbnail ] = useState([]);

	// const handleImage = (name) => (e) => {
	// 	const value = e.target.files[0];
	// 	let reader = new FileReader();
	// 	reader.onloadend = () => {
	// 		setThumbnail(reader.result);
	// 	};
	// 	reader.readAsDataURL(event.target.files[0]);
	// 	formData.set(name, value);
	// 	setValues({ ...values, [name]: value, formData, error: '' });
	// };

	const handleChange = (name) => (e) => {
		const value = e;
		formData.set(name, value);
		setValues({ ...values, [name]: value, formData, error: '' });
	};

	const handleImage = (file, callback) => {
		const reader = new FileReader();
		reader.onloadend = () => {
			callback(reader.result);
		};
		reader.readAsDataURL(file);
		formData.set('photo', file);
	};

	const [ fileInfo, setFileInfo ] = React.useState(null);

	const thumbnailStyles = {
		width: 300,
		height: 200,
		marginTop: 0
	};

	const handleImageArray = (event) => {
		setValues({ ...values, uploading: true });
		var fileInput = false;
		if (!event.target.files[0] || event.target.files[0].size > 19922944) {
			setValues({ ...values, error: 'Image size is too large!', uploading: false });
		}
		if (event.target.files[0]) {
			fileInput = true;
		}
		if (fileInput) {
			var img = event.target.files[0];
			imageupload(img).then((res) => {
				let imgArray = images;
				imgArray.push(res.data.id);
				setValues({ ...values, images: imgArray, uploading: false });
				formData.set('image', imgArray);
			});
		}
	};

	const deleteImage = (e, i) => {
		e.preventDefault();
		let imgArray = images;
		imgArray.splice(i, 1);
		setValues({ ...values, images: imgArray });
		formData.set('image', imgArray);
	};

	const handleBody = (e) => {
		setBody(e);
		formData.set('body', e);
		if (typeof window !== 'undefined') {
			localStorage.setItem('blog', JSON.stringify(e));
		}
	};

	const [ tempTags ] = useRef();

	const handleTagsChange = (e, { value }) => {
		formData.set('tags', e);
	};

	const [ tempCategories ] = useRef();

	const handleCategoriesChange = (e, { value }) => {
		formData.set('categories', e);
	};

	const uploaderStyles = {
		lineHeight: '200px'
	};

