import Link from 'next/link';
import renderHTML from 'react-render-html';
import { useState, useEffect } from 'react';
import { listSearch } from '../../actions/blog';

import { Form, FormGroup, FormControl, Grid, Row, Col } from 'rsuite';

const Search = () => {
    const [values, setValues] = useState({
        search: undefined,
        results: [],
        searched: false,
        message: ''
    });

    const { search, results, searched, message } = values;

    const searchSubmit = e => {
        e.preventDefault();
        listSearch({ search }).then(data => {
            setValues({ ...values, results: data, searched: true, message: `${data.length} blogs found` });
        });
    };

    const handleChange = e => {
        setValues({ ...values, search: e, searched: false, results: [] });
    };

    const searchedBlogs = (results = []) => {
        return (
            <div>
                {message && <p className="pt-4">{message}</p>}

                {results.map((blog, i) => {
                    return (
                        <div key={i}>
                            <Link href={`/blogs/${blog.slug}`}>
                                <a className="text-primary">{blog.title}</a>
                            </Link>
                        </div>
                    );
                })}
            </div>
        );
    };

    const searchForm = () => (
       <Form onSubmit={searchSubmit} layout="inline">
           <FormGroup>
               <FormControl placeholder="Search Posts..." onChange={handleChange} style={{marginTop: 10}} />
           </FormGroup>
           <Button type="submit">
                <Icon icon="search"  /> Search
            </Button>
       </Form>
    );

    return (
        <div className="container">
            <Grid fluid>
                <Row className="show-grid">
                    <Col xs="24">
                        {searchForm()}
                    </Col>
                </Row>
            </Grid>
            {searched && <div>{searchedBlogs(results)}</div>}
        </div>
    );
};

export default Search;
