import { useState } from 'react';
import { Menu, Segment } from 'semantic-ui-react';

const Topnav = () => {
    const [activeItem, activateItem] = useState('home')

    const segmentStyle = {
        borderRadius: 0
    }

    return (
        <Segment inverted style={ segmentStyle }>
            <Menu inverted pointing secondary>

                <Menu.Item
                    name='home'
                    active={activeItem === 'home'}
                    onClick={() => activateItem('home')}
                />
                <Menu.Item
                    name='messages'
                    active={activeItem === 'messages'}
                    onClick={() => activateItem('messages')}
                />
                <Menu.Item
                    name='friends'
                    active={activeItem === 'friends'}
                    onClick={() => activateItem('friends')}
                />
                <Menu.Menu position='right'>
                    <Menu.Item
                        name='logout'
                        active={activeItem === 'logout'}
                        onClick={() => activateItem('logout')}
                    />
                </Menu.Menu>
            </Menu>
        </Segment>
    )
}

export default Topnav;
