import Link from 'next/link';
import { useState, useEffect } from 'react';
import Router from 'next/router';
import { getCookie, isAuth } from '../../actions/auth';
import { list, removeBlog } from '../../actions/blog';
import moment from 'moment';

import { Grid, Row, Col, ButtonToolbar, Button, Icon, FlexboxGrid } from 'rsuite';

const BlogRead = ({ username }) => {
	const [ blogs, setBlogs ] = useState([]);
	const [ message, setMessage ] = useState('');
	const token = getCookie('token');

	useEffect(() => {
		loadBlogs();
	}, []);

	const loadBlogs = () => {
		list(username).then((data) => {
			if (data.error) {
				console.log(data.error);
			} else {
				setBlogs(data);
			}
		});
	};

	const deleteBlog = (slug) => {
		removeBlog(slug, token).then((data) => {
			if (data.error) {
				console.log(data.error);
			} else {
				setMessage(data.message);
				loadBlogs();
			}
		});
	};

	const deleteConfirm = (slug) => {
		let answer = window.confirm('Are you sure you want to delete your blog?');
		if (answer) {
			deleteBlog(slug);
		}
	};

	const showUpdateButton = (blog) => {
		if (isAuth() && isAuth().role === 0) {
			return (
				<Link href={`/user/crud/${blog.slug}`}>
					<a className="ml-2 btn btn-sm btn-warning">Update</a>
				</Link>
			);
		} else if (isAuth() && isAuth().role === 1) {
			return (
				<Link href={`/admin/crud/${blog.slug}`}>
					<a className="ml-2 btn btn-sm btn-warning">Update</a>
				</Link>
			);
		}
	};

	const showAllBlogs = () => {
		return blogs.map((blog, i) => {
			return (
				<FlexboxGrid key={i} className="manage-blog px-3 py-2 mb-2">
					<FlexboxGrid.Item colspan={18}>
						<h4>{blog.title}</h4>
						<p className="writer grey-text">
							<Icon icon="user" className="pr-2" /> {blog.postedBy.name}{' '}
							<Icon icon="calendar" className="px-2" /> {moment(blog.updatedAt).fromNow()}
						</p>
					</FlexboxGrid.Item>
					<FlexboxGrid.Item colspan={6}>
						<ButtonToolbar>
							<Button size="sm" color="yellow">
								<Icon icon="edit2" /> Edit
							</Button>
							<Button size="sm" color="red">
								<Icon icon="trash" /> Delete
							</Button>
						</ButtonToolbar>
					</FlexboxGrid.Item>
				</FlexboxGrid>
				// <div key={i} className="pb-5">
				//     <h3>{blog.title}</h3>
				//     <p className="mark">
				//         Written by {blog.postedBy.name} | Published on {moment(blog.updatedAt).fromNow()}
				//     </p>
				//     <button className="btn btn-sm btn-danger" onClick={() => deleteConfirm(blog.slug)}>
				//         Delete
				//     </button>
				//     {showUpdateButton(blog)}
				// </div>
			);
		});
	};

	return (
		<React.Fragment>
			<div className="container">
				<Grid fluid>
					<Row className="show-grid">{showAllBlogs()}</Row>
				</Grid>
				{/* <div className="col-md-12">
                    {message && <div className="alert alert-warning">{message}</div>}
                    {showAllBlogs()}
                </div> */}
			</div>
		</React.Fragment>
	);
};

export default BlogRead;
