import { useState, useEffect } from 'react';
import { Menu, Segment, Icon, Input } from 'semantic-ui-react';
import { signout, isAuth } from '../actions/auth';
import Router from 'next/router';
import { useLocation } from 'react-router-dom'

const Topnav = (props) => {
    const [activeItem, activateItem] = useState('home')

    const segmentStyle = {
        borderRadius: 0
    }

    const location = useLocation()

    useEffect(
        () => {
            console.log(location.pathname)
        },
        [location]
    )

    return (
        <Segment inverted style={segmentStyle}>
            <Menu inverted secondary>
                <Menu.Item>
                    <Icon name='grid layout' />
                </Menu.Item>
                <Menu.Item
                    name='Home'
                    icon='home'
                    active={activeItem === 'home'}
                    onClick={() => Router.replace('/')}
                />
                {!isAuth() && (
                    <>
                        <Menu.Item
                            name='Sign In'
                            icon='sign-in'
                            active={activeItem === 'signin'}
                            // active={props.history.location.pathname === "/signin" ? true : false}
                            onClick={() => Router.replace('/signin')}
                        />
                        <Menu.Item
                            name='Sign Up'
                            icon='add user'
                            active={activeItem === 'signup'}
                            onClick={() => Router.replace('/signup')}
                        />
                    </>
                )}
                {isAuth() && (
                    <Menu.Item
                        name='Sign Out'
                        icon='sign-out'
                        onClick={() => signout(() => Router.replace('/signin'))}
                    />
                )}

                <Menu.Item position='right'>
                    <Input
                        action={{ type: 'submit', content: 'Search' }}
                        placeholder='Type to search...'
                    />
                </Menu.Item>
            </Menu>
        </Segment>
    )
}

export default Topnav;
