import Link from 'next/link';
import { useState, useEffect, useRef } from 'react';
import Router from 'next/router';
import dynamic from 'next/dynamic';
import { withRouter } from 'next/router';
import { getCookie, isAuth } from '../../actions/auth';
import { getCategories } from '../../actions/category';
import { imageupload } from '../../actions/imgur';
import { getTags } from '../../actions/tag';
import { createBlog } from '../../actions/blog';
const ReactQuill = dynamic(() => import('react-quill'), { ssr: false });
import '../../node_modules/react-quill/dist/quill.snow.css';
import { QuillModules, QuillFormats } from '../../helpers/quill';
import { Form, Input, Button, Container, Grid, Dropdown, Icon, Modal, Header } from 'semantic-ui-react';

const CreateBlog = ({ router }) => {
	const blogFromLS = () => {
		if (typeof window === 'undefined') {
			return false;
		}

		if (localStorage.getItem('blog')) {
			return JSON.parse(localStorage.getItem('blog'));
		} else {
			return false;
		}
	};

	const [ categories, setCategories ] = useState([]);
	const [ tags, setTags ] = useState([]);

	const [ body, setBody ] = useState(blogFromLS());
	const [ values, setValues ] = useState({
		error: '',
		sizeError: '',
		success: '',
		formData: '',
		title: '',
		images: [],
		uploading: false,
	});

	const { error, sizeError, success, formData, title, images, uploading } = values;
	const token = getCookie('token');

	useEffect(
		() => {
			setValues({ ...values, formData: new FormData() });
			initCategories();
			initTags();
		},
		[ router ]
	);

	const initCategories = () => {
		getCategories().then((data) => {
			if (data.error) {
				setValues({ ...values, error: data.error });
			} else {
				const items = [];
				data.forEach((item, i) => {
					items[i] = {
						key: i,
						value: item._id,
						text: item.name
					};
				});
				setCategories(items);
			}
		});
	};

	const initTags = () => {
		getTags().then((data) => {
			if (data.error) {
				setValues({ ...values, error: data.error });
			} else {
				const items = [];
				data.forEach((item, i) => {
					items[i] = {
						key: i,
						value: item._id,
						text: item.name
					};
				});
				setTags(items);
			}
		});
	};

	const publishBlog = (e) => {
		e.preventDefault();
		setValues({ ...values, uploading: true });
		createBlog(formData, token).then((data) => {
			if (data.error) {
				setValues({ ...values, error: data.error });
			} else {
				setValues({ ...values, uploading: false, title: '', error: '', success: `A new blog titled "${data.title}" is created` });
				setBody('');
				setCategories([]);
				setTags([]);
				setThumbnail('');
			}
		});
	};

	const [ thumbnail, setThumbnail ] = useState([]);

	const handleChange = (name) => (e) => {
		const value = name === 'photo' ? e.target.files[0] : e.target.value;
		if (name === 'photo') {
			let reader = new FileReader();
			reader.onloadend = () => {
				setThumbnail(reader.result);
			};
			reader.readAsDataURL(event.target.files[0]);
		}
		formData.set(name, value);
		setValues({ ...values, [name]: value, formData, error: '' });
	};

	const handleImageArray = (event) => {
		setValues({ ...values, uploading: true });
		var fileInput = false;
		if (!event.target.files[0] || event.target.files[0].size > 19922944) {
			setValues({ ...values, error: 'Image size is too large!', uploading: false });
		}
		if (event.target.files[0]) {
			fileInput = true;
		}
		if (fileInput) {
			var img = event.target.files[0];
			imageupload(img).then((res) => {
				let imgArray = images;
				imgArray.push(res.data.id);
				setValues({ ...values, images: imgArray, uploading: false });
				formData.set('image', imgArray);
			});
		}
	};

	const handleBody = (e) => {
		setBody(e);
		formData.set('body', e);
		if (typeof window !== 'undefined') {
			localStorage.setItem('blog', JSON.stringify(e));
		}
	};

	const [ tempTags ] = useRef();

	const handleTagsChange = (e, { value }) => {
		formData.set('tags', value);
	};

	const [ tempCategories ] = useRef();

	const handleCategoriesChange = (e, { value }) => {
		formData.set('categories', value);
	};

	return (
		<Container>
			<Grid>
				<Grid.Column columns={16}>
					<Form onSubmit={publishBlog} loading={uploading ? true : false}>
						<Grid>
							<Grid.Column width={12}>
								<Form.Field>
									<Input
										type="text"
										placeholder="Write an awesome title..."
										value={title}
										onChange={handleChange('title')}
									/>
								</Form.Field>
								<Form.Field>
									<ReactQuill
										modules={QuillModules}
										formats={QuillFormats}
										value={body}
										placeholder="Write something amazing..."
										onChange={handleBody}
									/>
								</Form.Field>
								<Grid columns="equal" style={{ paddingBottom: 25 }}>
									<Grid.Column columns={8}>
										<Dropdown
											placeholder="Tags"
											fluid
											multiple
											search
											selection
											options={tags}
											value={tempTags}
											onChange={handleTagsChange}
										/>
									</Grid.Column>
									<Grid.Column columns={8}>
										<Dropdown
											placeholder="Categories"
											fluid
											search
											selection
											options={categories}
											value={tempCategories}
											onChange={handleCategoriesChange}
										/>
									</Grid.Column>
								</Grid>
							</Grid.Column>
							<Grid.Column width={4}>
								<Form.Field>
									{thumbnail.length > 10 && <img className="thumbnailImage" src={thumbnail} />}
									<div class="choose_file">
										<Button secondary>
											<Icon name="image" /> Upload Thumbnail
										</Button>
										<input onChange={handleChange('photo')} type="file" accept="image/*" />
									</div>
								</Form.Field>
								<Form.Field>
								{images.map((image,i) => {
										return (<img className="thumbnailImage" src={`https://i.imgur.com/${image}m.png`} key={i} />)
									})}
									<div class="choose_file">
										<Button secondary loading={uploading ? true : false}>
											<Icon name="image" /> Upload Images
										</Button>
										<input onChange={handleImageArray} type="file" accept="image/*" />
									</div>
								</Form.Field>
							</Grid.Column>
						</Grid>

						<Button type="submit" primary style={{ width: '100%' }}>
							Publish
						</Button>
					</Form>
				</Grid.Column>
			</Grid>

			<Modal open={error} onClose={() => setValues({ ...values, error: '' })} basic size="tiny">
				<Header icon="browser" content="Sign-in Successful" />
				<Modal.Content>{error}</Modal.Content>
				<Modal.Actions>
					<Button color="green" onClick={() => setValues({ ...values, error: '' })} inverted>
						<Icon name="checkmark" /> Got it
					</Button>
				</Modal.Actions>
			</Modal>

			<Modal open={success} onClose={() => setValues({ ...values, success: '' })} basic size="tiny">
				<Header icon="browser" content="Sign-in Successful" />
				<Modal.Content>{success}</Modal.Content>
				<Modal.Actions>
					<Button color="green" onClick={() => setValues({ ...values, success: '' })} inverted>
						<Icon name="checkmark" /> Got it
					</Button>
				</Modal.Actions>
			</Modal>
		</Container>
	);
};

export default withRouter(CreateBlog);
