import Link from 'next/link';
import { useState, useEffect } from 'react';
import Router from 'next/router';
import { getCookie, isAuth, updateUser } from '../../actions/auth';
import { getProfile, update } from '../../actions/user';
import { API } from '../../config';
import { imageupload } from '../../actions/imgur';

import {
	Form,
	FormGroup,
	FormControl,
	ControlLabel,
	HelpBlock,
	Grid,
	Row,
	Col,
	SelectPicker,
	Button,
	FlexboxGrid,
	Icon
} from 'rsuite';
import FlexboxGridItem from 'rsuite/lib/FlexboxGrid/FlexboxGridItem';

const ProfileUpdate = () => {
	const [ values, setValues ] = useState({
		username: '',
		name: '',
		email: '',
		avatar: '',
		cover: '',
		about: '',
		blood: '',
		contact: '',
		address: '',
		password: '',
		error: false,
		success: false,
		loading: false,
		photo: '',
		userData: ''
	});

	const token = getCookie('token');
	const {
		username,
		name,
		email,
		avatar,
		cover,
		about,
		blood,
		contact,
		address,
		password,
		error,
		success,
		loading,
		photo,
		userData
	} = values;

	const init = () => {
		getProfile(token).then((data) => {
			if (data.error) {
				setValues({ ...values, error: data.error });
			} else {
				setValues({
					...values,
					username: data.username,
					name: data.name,
					email: data.email,
					avatar: data.avatar,
					cover: data.cover,
					about: data.about,
					blood: data.blood,
					contact: data.contact,
					address: data.address,
					userData: new FormData()
				});
			}
		});
	};

	useEffect(() => {
		init();
	}, []);

	const [ bloodGroups, setBloodGroups ] = useState([
		{ label: 'A+', value: 'A+' },
		{ label: 'A-', value: 'A-' },
		{ label: 'B+', value: 'B+' },
		{ label: 'B-', value: 'B-' },
		{ label: 'AB+', value: 'AB+' },
		{ label: 'AB-', value: 'AB-' },
		{ label: 'O+', value: 'O+' },
		{ label: 'O-', value: 'O-' }
	]);

	const handleChange = (name) => (e) => {
		const value = name === 'photo' ? e.target.files[0] : e;
		userData.set(name, value);
		setValues({ ...values, [name]: value, userData: userData, error: false, success: false });
	};

	const handleBloodChange = (e, { value }) => {
		userData.set('blood', e);
		setValues({ ...values, blood: e });
	};

	const handleSubmit = (e) => {
		e.preventDefault();
		setValues({ ...values, loading: true });
		update(token, userData).then((data) => {
			if (data.error) {
				setValues({ ...values, error: data.error, success: false, loading: false });
			} else {
				updateUser(data, () => {
					setValues({
						...values,
						username: data.username,
						name: data.name,
						email: data.email,
						avatar: data.avatar,
						cover: data.cover,
						about: data.about,
						password: '',
						blood: data.blood,
						contact: data.contact,
						address: data.address,
						success: true,
						loading: false
					});
				});
			}
		});
	};

	const handleCoverUpload = (event) => {
		setValues({ ...values, loading: true });
		var fileInput = false;
		if (!event.target.files[0] || event.target.files[0].size > 19922944) {
			setValues({ ...values, error: 'Image size is too large!', loading: false });
		}
		if (event.target.files[0]) {
			fileInput = true;
		}
		if (fileInput) {
			var img = event.target.files[0];
			imageupload(img).then((res) => {
				setValues({ ...values, cover: res.data.id, loading: false });
				userData.set('cover', res.data.id);
			});
		}
	};

	return (
		<React.Fragment>
			<Grid fluid>
				<Row className="show-grid my-3">
					<Col xs={24}>
						<Form fluid onSubmit={handleSubmit}>
							<FlexboxGrid>
								<FlexboxGrid.Item colspan={12}>
									<FlexboxGrid>
										<FlexboxGridItem colspan={24} className="cover-upload">
											{cover && (
												<img
													src={`https://i.imgur.com/${cover}m.png`}
												/>
											)}
											<Button color="cyan">
												<Icon icon="upload" /> Upload
											</Button>
                                            <input
												onChange={handleCoverUpload}
												type="file"
												accept="image/*"
												className={loading ? 'disabled-input' : ''}
											/>
										</FlexboxGridItem>
									</FlexboxGrid>
								</FlexboxGrid.Item>
								<FlexboxGrid.Item colspan={12}>
									<FormGroup>
										<ControlLabel>Username</ControlLabel>
										<FormControl value={username} onChange={handleChange('username')} />
										<HelpBlock>Required</HelpBlock>
									</FormGroup>
									<FormGroup>
										<ControlLabel>Full Name</ControlLabel>
										<FormControl value={name} onChange={handleChange('name')} />
										<HelpBlock>Required</HelpBlock>
									</FormGroup>
									<FormGroup>
										<ControlLabel>Email</ControlLabel>
										<FormControl value={email} onChange={handleChange('email')} />
										<HelpBlock>Required</HelpBlock>
									</FormGroup>
									<FormGroup>
										<ControlLabel>About</ControlLabel>
										<FormControl
											placeholder="Write about yourself"
											componentClass="textarea"
											rows={5}
											value={about}
											onChange={handleChange('about')}
										/>
									</FormGroup>
									<FormGroup>
										<SelectPicker
											block
											data={bloodGroups}
											value={blood}
											onChange={handleBloodChange}
											placeholder="Blood Group"
										/>
									</FormGroup>
									<FormGroup>
										<ControlLabel>Contact No</ControlLabel>
										<FormControl value={contact} onChange={handleChange('contact')} />
									</FormGroup>
									<FormGroup>
										<ControlLabel>Address</ControlLabel>
										<FormControl value={address} onChange={handleChange('address')} />
									</FormGroup>
									<FormGroup>
										<ControlLabel>Password</ControlLabel>
										<FormControl
											value={password}
											type="password"
											onChange={handleChange('password')}
										/>
									</FormGroup>
								</FlexboxGrid.Item>
							</FlexboxGrid>

							<Button
								appearance="primary"
								block
								type="submit"
								className="my-3"
								disabled={
									name.length === 0 || username.length === 0 || email.length === 0 ? true : false
								}
							>
								Submit
							</Button>
						</Form>
					</Col>
				</Row>
			</Grid>
			{/* <div className="container">
                <div className="row">
                    <div className="col-md-4">
                        <img
                            src={`${API}/user/photo/${username}`}
                            className="img img-fluid img-thumbnail mb-3"
                            style={{ maxHeight: 'auto', maxWidth: '100%' }}
                            alt="user profile"
                        />
                    </div>
                    <div className="col-md-8 mb-5">
                        {showSuccess()}
                        {showError()}
                        {showLoading()}
                        {profileUpdateForm()}
                    </div>
                </div>
            </div> */}
		</React.Fragment>
	);
};

export default ProfileUpdate;
