import Link from 'next/link';
import { useState, useEffect, useRef } from 'react';
import Router from 'next/router';
import dynamic from 'next/dynamic';
import { withRouter } from 'next/router';
import { getCookie, isAuth } from '../../actions/auth';
import { getCategories } from '../../actions/category';
import { getTags } from '../../actions/tag';
import { singleBlog, updateBlog } from '../../actions/blog';
const ReactQuill = dynamic(() => import('react-quill'), { ssr: false });
import '../../node_modules/react-quill/dist/quill.snow.css';
import { QuillModules, QuillFormats } from '../../helpers/quill';
import { API } from '../../config';
import { imageupload } from '../../actions/imgur';

import {
	Form,
	FormGroup,
	FormControl,
	ControlLabel,
	HelpBlock,
	TagPicker,
	FlexboxGrid,
	SelectPicker,
	Uploader,
	Icon,
	Loader,
	Button,
	ButtonToolbar,
	Modal
} from 'rsuite';

const BlogUpdate = ({ router }) => {
	const [ body, setBody ] = useState('');
    const [ title, setTitle ] = useState('');
    const [ loading, setLoading ] = useState(true);

	const [ categories, setCategories ] = useState([]);
	const [ tags, setTags ] = useState([]);

	const [ checkedCat, setCheckedCat ] = useState([]); // categories
	const [ checkedTag, setCheckedTag ] = useState([]); // tags

	const [ values, setValues ] = useState({
		error: '',
		success: '',
		formData: '',
		initial: true,
		uploading: false,
        images: [],
	});

	const { error, success, formData, initial, uploading, images } = values;
	const token = getCookie('token');

	useEffect(
		() => {
			setValues({ ...values, formData: new FormData() });
			initBlog();
			initCategories();
            initTags();
            setLoading(false)
		},
		[ router ]
	);

	const initBlog = () => {
		if (router.query.slug) {
			singleBlog(router.query.slug).then((data) => {
				if (data.error) {
					console.log(data.error);
				} else {
					setTitle(data.title);
					setImages(data.image[0]);
					setBody(data.body);
					setCategoriesArray(data.categories);
					setTagsArray(data.tags);
				}
			});
		}
	};

	const setImages = (data) => {
		let arr = data.split(',');
		setValues({ ...values, images: arr });
		formData.set('image', arr);
	};

	const setCategoriesArray = (blogCategories) => {
		setCheckedCat(blogCategories[0]._id);
	};

	const setTagsArray = (blogTags) => {
		let ta = [];
		blogTags.map((t, i) => {
			ta.push(t._id);
		});
		setCheckedTag(ta);
	};

	const initCategories = () => {
		getCategories().then((data) => {
			if (data.error) {
				setValues({ ...values, error: data.error });
			} else {
				const items = [];
				data.forEach((item, i) => {
					items[i] = {
						key: i,
						value: item._id,
						label: item.name
					};
				});
				setCategories(items);
			}
		});
	};

	const initTags = () => {
		getTags().then((data) => {
			if (data.error) {
				setValues({ ...values, error: data.error });
			} else {
				const items = [];
				data.forEach((item, i) => {
					items[i] = {
						key: i,
						value: item._id,
						label: item.name
					};
				});
				setTags(items);
			}
		});
	};

	const handleToggle = (c) => () => {
		setValues({ ...values, error: '' });
		// return the first index or -1
		const clickedCategory = checked.indexOf(c);
		const all = [ ...checked ];

		if (clickedCategory === -1) {
			all.push(c);
		} else {
			all.splice(clickedCategory, 1);
		}
		console.log(all);
		setChecked(all);
		formData.set('categories', all);
	};

	const handleTagsToggle = (t) => () => {
		setValues({ ...values, error: '' });
		// return the first index or -1
		const clickedTag = checkedTag.indexOf(t);
		const all = [ ...checkedTag ];

		if (clickedTag === -1) {
			all.push(t);
		} else {
			all.splice(clickedTag, 1);
		}
		console.log(all);
		setCheckedTag(all);
		formData.set('tags', all);
	};

	const findOutCategory = (c) => {
		const result = checked.indexOf(c);
		if (result !== -1) {
			return true;
		} else {
			return false;
		}
	};

	const findOutTag = (t) => {
		const result = checkedTag.indexOf(t);
		if (result !== -1) {
			return true;
		} else {
			return false;
		}
	};

	const showCategories = () => {
		return (
			categories &&
			categories.map((c, i) => (
				<li key={i} className="list-unstyled">
					<input
						onChange={handleToggle(c._id)}
						checked={findOutCategory(c._id)}
						type="checkbox"
						className="mr-2"
					/>
					<label className="form-check-label">{c.name}</label>
				</li>
			))
		);
	};

	const showTags = () => {
		return (
			tags &&
			tags.map((t, i) => (
				<li key={i} className="list-unstyled">
					<input
						onChange={handleTagsToggle(t._id)}
						checked={findOutTag(t._id)}
						type="checkbox"
						className="mr-2"
					/>
					<label className="form-check-label">{t.name}</label>
				</li>
			))
		);
	};

	const handleChange = (name) => (e) => {
		// console.log(e.target.value);
		const value = e;
		formData.set(name, value);
		setValues({ ...values, [name]: value, formData, error: '' });
	};

	const handleTitle = (e) => {
		setTitle(e);
		formData.set('title', e);
	};

	const handleBody = (e) => {
		setBody(e);
		formData.set('body', e);
	};

	const editBlog = (e) => {
		e.preventDefault();
		updateBlog(formData, token, router.query.slug).then((data) => {
			if (data.error) {
				setValues({ ...values, error: data.error });
			} else {
				setValues({ ...values, success: `Blog titled "${data.title}" is successfully updated` });
				setTitle('');
				setBody('');
				if (isAuth() && isAuth().role === 1) {
					// Router.replace(`/admin/crud/${router.query.slug}`);
					Router.replace(`/admin`);
				} else if (isAuth() && isAuth().role === 0) {
					// Router.replace(`/user/crud/${router.query.slug}`);
					Router.replace(`/user`);
				}
			}
		});
	};

	const showError = () => (
		<div className="alert alert-danger" style={{ display: error ? '' : 'none' }}>
			{error}
		</div>
	);

	const showSuccess = () => (
		<div className="alert alert-success" style={{ display: success ? '' : 'none' }}>
			{success}
		</div>
	);

	const handleTagsChange = (e, { value }) => {
		formData.set('tags', e);
		setCheckedTag(e);
	};

	const handleCatChange = (e, { value }) => {
		formData.set('categories', e);
		setCheckedCat(e);
	};

	const handleImage = (file, callback) => {
		setValues({ ...values, initial: false });
		const reader = new FileReader();
		reader.onloadend = () => {
			callback(reader.result);
		};
		reader.readAsDataURL(file);
		formData.set('photo', file);
	};

	const thumbnailStyles = {
		width: 300,
		height: 200,
		marginTop: 0
	};

	const [ fileInfo, setFileInfo ] = React.useState(null);

	const handleImageArray = (event) => {
		setValues({ ...values, uploading: true });
		var fileInput = false;
		if (!event.target.files[0] || event.target.files[0].size > 19922944) {
			setValues({ ...values, error: 'Image size is too large!', uploading: false });
		}
		if (event.target.files[0]) {
			fileInput = true;
		}
		if (fileInput) {
			var img = event.target.files[0];
			imageupload(img).then((res) => {
				let imgArray = images;
				imgArray.push(res.data.id);
				setValues({ ...values, images: imgArray, uploading: false });
				formData.set('image', imgArray);
			});
		}
	};

	const deleteImage = (e, i) => {
		e.preventDefault();
		let imgArray = images;
		imgArray.splice(i, 1);
		setValues({ ...values, images: imgArray });
		formData.set('image', imgArray);
	};

	return (
		<React.Fragment>
			<Form fluid onSubmit={editBlog}>
				<FormGroup>
					<ControlLabel>
						<h6>Post Title</h6>
					</ControlLabel>
					<FormControl placeholder="Give an awesome title..." value={title} onChange={handleTitle} />
					<HelpBlock>Required</HelpBlock>
				</FormGroup>
				<FormGroup>
					<ControlLabel>
						<h6>Post Body</h6>
					</ControlLabel>
					<ReactQuill
						modules={QuillModules}
						formats={QuillFormats}
						value={body}
						placeholder="Write something amazing"
						onChange={handleBody}
					/>
					<HelpBlock>Required</HelpBlock>
				</FormGroup>
				<FormGroup>
					<FlexboxGrid>
						<FlexboxGrid.Item colspan={11}>
							<TagPicker
								block
								data={tags}
								value={checkedTag}
								onChange={handleTagsChange}
								placeholder="Select Tags"
							/>
							<HelpBlock>Required</HelpBlock>
						</FlexboxGrid.Item>
						<FlexboxGrid.Item colspan={2} />
						<FlexboxGrid.Item colspan={11}>
							<SelectPicker
								block
								data={categories}
								value={checkedCat}
								onChange={handleCatChange}
								placeholder="Select Categories"
							/>
							<HelpBlock>Required</HelpBlock>
						</FlexboxGrid.Item>
					</FlexboxGrid>
					<FlexboxGrid>
						<FlexboxGrid.Item colspan={10}>
							<ControlLabel>
								<h6 className="pt-3">Select Thumbnail</h6>
							</ControlLabel>
							<Uploader
								fileListVisible={false}
								listType="picture"
								onUpload={(file) => {
									handleImage(file.blobFile, (value) => {
										setFileInfo(value);
									});
								}}
							>
								<button style={thumbnailStyles} type="button">
									{fileInfo ? <img src={fileInfo} className="thumbnail-image" /> : ''}
									{body &&
									initial && (
										<img
											src={`${API}/blog/photo/${router.query.slug}`}
											className="thumbnail-image"
										/>
									)}
								</button>
							</Uploader>
							<HelpBlock>Required</HelpBlock>
						</FlexboxGrid.Item>
					</FlexboxGrid>
				</FormGroup>
				<FormGroup>
					<FlexboxGrid.Item colspan={24}>
						<ControlLabel>
							<h6>Add Images</h6>
						</ControlLabel>
						<div className="imgur-uploader">
							<FlexboxGrid>
								{images.map((image, i) => {
									return (
										<FlexboxGrid.Item key={i}>
											<div
												className="add-image-grid"
												style={{ display: 'grid', marginRight: '10px', marginBottom: '10px' }}
											>
												<img
													className="imgur-Image"
													src={`https://i.imgur.com/${image}m.png`}
													key={i}
												/>
												<Button color="red" size="xs" onClick={(e) => deleteImage(e, i)}>
													Delete
												</Button>
											</div>
										</FlexboxGrid.Item>
									);
								})}
								<FlexboxGrid.Item>
									<input
										onChange={handleImageArray}
										type="file"
										accept="image/*"
										className={uploading ? 'disabled-input' : ''}
									/>
									<Icon icon="upload" />
								</FlexboxGrid.Item>
							</FlexboxGrid>
						</div>
					</FlexboxGrid.Item>
					<HelpBlock>Optional</HelpBlock>
				</FormGroup>
				<FormGroup>
					<ButtonToolbar>
						<Button appearance="primary" block type="submit" disabled={uploading ? true : false}>
							Submit
						</Button>
					</ButtonToolbar>
				</FormGroup>
			</Form>
			<Modal
				backdrop="static"
				show={error.length > 0 ? true : false}
				onHide={() => setValues({ ...values, error: '' })}
			>
				<Modal.Header>
					<Modal.Title>
						<Icon icon="exclamation-circle" style={{ color: '#ffc845' }} size="lg" className="pr-2" />
						Error Occured
					</Modal.Title>
				</Modal.Header>
				<Modal.Body>{error}</Modal.Body>
				<Modal.Footer>
					<Button onClick={() => setValues({ ...values, error: '' })} appearance="primary">
						OK, Got it
					</Button>
				</Modal.Footer>
			</Modal>
			<Modal backdrop="static" show={success.length > 0 ? true : false} onHide={() => Router.replace('/blogs')}>
				<Modal.Header>
					<Modal.Title>
						<Icon icon="check" style={{ color: '#00a78e' }} size="lg" className="pr-2" />
						Successful
					</Modal.Title>
				</Modal.Header>
				<Modal.Body>{success}</Modal.Body>
				<Modal.Footer>
					<Button onClick={() => Router.replace('/blogs')} appearance="primary">
						OK, Got it
					</Button>
				</Modal.Footer>
			</Modal>
			<Modal backdrop="static" show={uploading} size="xs">
				<Modal.Body className="text-center">
					<Loader size="lg" />
					<h4 className="py-3">Uploading</h4>
				</Modal.Body>
			</Modal>
            <Modal backdrop="static" show={loading} size="xs">
				<Modal.Body className="text-center">
					<Loader size="lg" />
					<h4 className="py-3">Loading Contents...</h4>
				</Modal.Body>
			</Modal>
		</React.Fragment>
	);
};

export default withRouter(BlogUpdate);
