import Link from 'next/link';
import { useState, useEffect } from 'react';
import Router from 'next/router';
import dynamic from 'next/dynamic';
import { withRouter } from 'next/router';
import { getCookie, isAuth } from '../../actions/auth';
import { getCategories } from '../../actions/category';
import { getTags } from '../../actions/tag';
import { createBlog } from '../../actions/blog';
const ReactQuill = dynamic(() => import('react-quill'), { ssr: false });
import '../../node_modules/react-quill/dist/quill.snow.css';
import { QuillModules, QuillFormats } from '../../helpers/quill';
import { Form, Input, Button, Container, Grid, Dropdown } from 'semantic-ui-react';

const CreateBlog = ({ router }) => {
	const blogFromLS = () => {
		if (typeof window === 'undefined') {
			return false;
		}

		if (localStorage.getItem('blog')) {
			return JSON.parse(localStorage.getItem('blog'));
		} else {
			return false;
		}
	};

	const [ categories, setCategories ] = useState([]);
	const [ tags, setTags ] = useState([]);

	const [ checked, setChecked ] = useState([]); // categories
	const [ checkedTag, setCheckedTag ] = useState([]); // tags

	const [ body, setBody ] = useState(blogFromLS());
	const [ values, setValues ] = useState({
		error: '',
		sizeError: '',
		success: '',
		formData: '',
		title: '',
        hidePublishButton: false,
    });

	const { error, sizeError, success, formData, title, hidePublishButton, selectedTags } = values;
	const token = getCookie('token');

	useEffect(
		() => {
			setValues({ ...values, formData: new FormData() });
			initCategories();
			initTags();
		},
		[ router ]
	);

	const initCategories = () => {
		getCategories().then((data) => {
			if (data.error) {
				setValues({ ...values, error: data.error });
			} else {
				setCategories(data);
			}
		});
	};

	const initTags = () => {
		getTags().then((data) => {
			if (data.error) {
				setValues({ ...values, error: data.error });
			} else {
				const items = [];
				data.forEach((item, i) => {
					items[i] = {
						key: i,
						value: item._id,
						text: item.name
					};
				});
				setTags(items);
			}
		});
	};

	const publishBlog = (e) => {
		e.preventDefault();
		createBlog(formData, token).then((data) => {
			if (data.error) {
				setValues({ ...values, error: data.error });
			} else {
				setValues({ ...values, title: '', error: '', success: `A new blog titled "${data.title}" is created` });
				setBody('');
				setCategories([]);
				setTags([]);
			}
		});
	};

	const handleChange = (name) => (e) => {
		const value = name === 'photo' ? e.target.files[0] : e.target.value;
		formData.set(name, value);
		setValues({ ...values, [name]: value, formData, error: '' });
	};

	const handleBody = (e) => {
		setBody(e);
		formData.set('body', e);
		if (typeof window !== 'undefined') {
			localStorage.setItem('blog', JSON.stringify(e));
		}
	};

	const handleToggle = (c) => () => {
		setValues({ ...values, error: '' });
		// return the first index or -1
		const clickedCategory = checked.indexOf(c);
		const all = [ ...checked ];

		if (clickedCategory === -1) {
			all.push(c);
		} else {
			all.splice(clickedCategory, 1);
		}
		// console.log(all);
		setChecked(all);
		formData.set('categories', all);
	};

	const handleTags = (e) => () => {
		setTempTags({ ...tempTags });
		// return the first index or -1
		// const clickedTag = checkedTag.includes(t);
		// const all = [ ...checkedTag ];
		// if (clickedTag === false) {
		// 	all.push(t);
		// } else {
		// 	all.splice(clickedTag, 1);
		// }
		// // console.log(all);
		// setCheckedTag(all);
        // formData.set('tags', all);
        console.log(e)
	};

	const showCategories = () => {
		return (
			categories &&
			categories.map((c, i) => (
				<li key={i} className="list-unstyled">
					<input onChange={handleToggle(c._id)} type="checkbox" className="mr-2" />
					<label className="form-check-label">{c.name}</label>
				</li>
			))
		);
	};

	const showError = () => (
		<div className="alert alert-danger" style={{ display: error ? '' : 'none' }}>
			{error}
		</div>
	);

	const showSuccess = () => (
		<div className="alert alert-success" style={{ display: success ? '' : 'none' }}>
			{success}
		</div>
    );

    const [tempTags] = useRef()

	return (
		<Container>
			<Grid>
				<Form onSubmit={publishBlog}>
					<Form.Field>
						<Input
							type="text"
							placeholder="Write an awesome title..."
							value={title}
							onChange={handleChange('title')}
						/>
					</Form.Field>
					<Form.Field>
						<ReactQuill
							modules={QuillModules}
							formats={QuillFormats}
							value={body}
							placeholder="Write something amazing..."
							onChange={handleBody}
						/>
					</Form.Field>

					{/* <div style={{marginTop: 15, marginBottom: 10}}>
						{tags &&
							tags.map((t, i) => 
                                    <Checkbox key={i} label={t.name} onChange={handleTagsToggle(t._id)} style={{paddingRight: 15}} />
                                )}
					</div> */}
					<Dropdown placeholder="Tags" fluid multiple search selection options={tags} value={tempTags} />

					<Button type="submit" primary>
						Publish
					</Button>
				</Form>
			</Grid>
		</Container>
		// <div className="container-fluid pb-5">
		// 	<div className="row">
		// 		<div className="col-md-8">
		// 			{createBlogForm()}
		// 			<div className="pt-3">
		// 				{showError()}
		// 				{showSuccess()}
		// 			</div>
		// 		</div>

		// 		<div className="col-md-4">
		// 			<div>
		// 				<div className="form-group pb-2">
		// 					<h5>Featured image</h5>
		// 					<hr />

		// 					<small className="text-muted">Max size: 1mb</small>
		// 					<br />
		// 					<label className="btn btn-outline-info">
		// 						Upload featured image
		// 						<input onChange={handleChange('photo')} type="file" accept="image/*" hidden />
		// 					</label>
		// 				</div>
		// 			</div>
		// 			<div>
		// 				<h5>Categories</h5>
		// 				<hr />

		// 				<ul style={{ maxHeight: '200px', overflowY: 'scroll' }}>{showCategories()}</ul>
		// 			</div>
		// 			<div>
		// 				<h5>Tags</h5>
		// 				<hr />
		// 				<ul style={{ maxHeight: '200px', overflowY: 'scroll' }}>{showTags()}</ul>
		// 			</div>
		// 		</div>
		// 	</div>
		// </div>
	);
};

export default withRouter(CreateBlog);
