import { useState } from 'react';
import { Menu, Segment, Icon, Input } from 'semantic-ui-react';
import { signout, isAuth } from '../actions/auth';
import Router from 'next/router';

const Topnav = () => {
    const [activeItem, activateItem] = useState('home')

    const segmentStyle = {
        borderRadius: 0
    }

    const handleClick = (item, route) => {
        activateItem({activateItem: item})

        return <>Router.replace(route)</>
    }
    
    return (
        <Segment inverted style={segmentStyle}>
            <Menu inverted pointing secondary>
                <Menu.Item>
                    <Icon name='grid layout' />
                </Menu.Item>
                <Menu.Item
                    name='Home'
                    icon='home'
                    active={activeItem === 'home'}
                    onClick={() => Router.replace('/')}
                />
                <Menu.Item
                    name='Sign In'
                    icon='sign-in'
                    active={activeItem === 'signin'}
                    // onClick={() => Router.replace('/signin')}
                    onClick={handleClick('signin', 'signin')}
                />
                {!isAuth() && (
                    <Menu.Item
                        name='Sign Up'
                        icon='add user'
                        active={activeItem === 'signup'}
                        onClick={() => Router.replace('/signup')}
                    />
                )}
                {isAuth() && (
                    <Menu.Item
                        name='Sign Out'
                        icon='sign-out'
                        onClick={() => signout(() => Router.replace('/signin'))}
                    />
                )}

                <Menu.Item position='right'>
                    <Input
                        action={{ type: 'submit', content: 'Search' }}
                        placeholder='Type to search...'
                    />
                </Menu.Item>
            </Menu>
        </Segment>
    )
}

export default Topnav;
