import { useState } from 'react';
import { Menu, Segment, Icon, Input } from 'semantic-ui-react';
import { signout, isAuth } from '../actions/auth';
import Router from 'next/router';
import { useRouter } from 'next/router';
import NProgress from 'nprogress';

Router.onRouteChangeStart = url => NProgress.start();
Router.onRouteChangeComplete = url => NProgress.done();
Router.onRouteChangeError = url => NProgress.done();

const Topnav = () => {
    const [activeItem, activateItem] = useState('home')

    const segmentStyle = {
        borderRadius: 0,
        marginTop: 0,
        padding: 5
    }

    const router = useRouter()

    return (
        <Segment inverted style={segmentStyle} color="pink" >
            <Menu inverted secondary color="pink">
                <Menu.Item>
                    <Icon name='grid layout' />
                </Menu.Item>
                <Menu.Item
                    name='Home'
                    icon='home'
                    active={router.pathname === "/" ? true : false}
                    onClick={() => Router.replace('/')}
                />
                {!isAuth() && (
                    <>
                        <Menu.Item
                            name='Sign In'
                            icon='sign-in'
                            active={router.pathname === "/signin" ? true : false}
                            onClick={() => Router.replace('/signin')}
                        />
                        <Menu.Item
                            name='Sign Up'
                            icon='add user'
                            active={router.pathname === "/signup" ? true : false}
                            onClick={() => Router.replace('/signup')}
                        />
                    </>
                )}

                <Menu.Item position='right'>
                    <Input
                        action={{ type: 'submit', content: 'Search' }}
                        placeholder='Type to search...'
                    />
                </Menu.Item>
                {isAuth() && isAuth().role === 0 && (
                    <Menu.Item position="right"
                        name={`${isAuth().name}`}
                        icon='user circle'
                        active={router.pathname === "/user" ? true : false}
                        onClick={() => Router.replace('/user')}
                    />
                )}
                {isAuth() && isAuth().role === 1 && (
                    <Menu.Item position="right"
                        name={`${isAuth().name}`}
                        icon='user circle'
                        active={router.pathname === "/admin" ? true : false}
                        onClick={() => Router.replace('/admin')}
                    />
                )}
                {isAuth() && (
                    <Menu.Item
                        name='Sign Out'
                        icon='sign-out'
                        onClick={() => signout(() => Router.replace('/signin'))}
                    />
                )}
            </Menu>
        </Segment>
    )
}

export default Topnav;
