import { useState, useEffect } from 'react';
import { signin, authenticate, isAuth } from '../../actions/auth';
import Link from 'next/link';
import Router from 'next/router';

import {
	Form,
	FormGroup,
	FormControl,
	ControlLabel,
	HelpBlock,
	Icon,
	Button,
	ButtonToolbar,
	Grid,
	Row,
	Col,
	Modal
} from 'rsuite';

const SigninComponent = () => {
	const [ values, setValues ] = useState({
		email: '',
		password: '',
		error: false,
		checking: false,
		message: false,
		showForm: true
	});

	const { email, password, error, checking, message } = values;

	const headerStyle = {
		textAlign: 'center'
	};
	const subHeaderStyle = {
		paddingLeft: 30,
		paddingRight: 30,
		paddingTop: 15,
		color: 'grey'
	};

	useEffect(() => {
		isAuth() && Router.replace('/');
	}, []);

	const handleSubmit = (e) => {
		e.preventDefault();
		setValues({ ...values, checking: true, error: false });
		const user = { email, password };

		signin(user).then((data) => {
			if (data.error) {
				setValues({ ...values, error: data.error });
			} else {
				authenticate(data, () => {
					setValues({
						...values,
						email: '',
						password: '',
						checking: false,
						error: false,
						message: true
					});
				});
			}
		});
	};

	const handleChange = (name) => (e) => {
		setValues({ ...values, error: false, [name]: e });
	};

	const handleClose = () => {
		setValues({ ...values, error: '' });
	};

	const pushHome = () => {
		setValues({ ...values, message: '' });
		if (isAuth() && isAuth().role === 1) {
			Router.replace('/admin');
		} else {
			Router.replace('/user');
		}
	};

	return (
		<React.Fragment>
			<Grid fluid>
				<Row className="show-grid">
					<Col xs={12} />
					<Col xs={12}>
						<div className="text-center">
							<img style={{ maxWidth: '100px' }} src="/images/comicsghor.png" />
						</div>
						<div className="py-3">
							<h4 className="text-center">Welcome to Comics Ghor</h4>
							<h6 className="text-center">Fill out the form below to sign-up for a new account</h6>
						</div>
						<div>
							<Form fluid onSubmit={handleSubmit}>
								<FormGroup>
									<ControlLabel>Email</ControlLabel>
									<FormControl
										placeholder="Email"
										name="email"
										type="email"
										value={email}
										onChange={handleChange('email')}
									/>
									<HelpBlock>
										{/\S+@\S+\.\S+/.test(email) ? (
											<span>
												<Icon icon="check" style={{ color: 'green' }} /> Email address seems
												valid
											</span>
										) : (
											<span>
												<Icon icon="close" style={{ color: 'red' }} /> Provide a valid email
												address
											</span>
										)}
									</HelpBlock>
								</FormGroup>
								<FormGroup>
									<ControlLabel>Password</ControlLabel>
									<FormControl
										placeholder="Password"
										name="password"
										type="password"
										value={password}
										onChange={handleChange('password')}
									/>
									<HelpBlock>
										{password.length < 6 ? (
											<span>
												<Icon icon="close" style={{ color: 'red' }} /> Weak password
											</span>
										) : (
											<span>
												<Icon icon="check" style={{ color: 'green' }} /> Password seems valid
											</span>
										)}
									</HelpBlock>
								</FormGroup>
								<FormGroup>
									<ButtonToolbar>
										<Button appearance="primary" type="submit">Submit</Button>
									</ButtonToolbar>
								</FormGroup>
							</Form>
							<h6 className="text-center py-2">
								Don't have an account? <Link href="/signup">Sign Up</Link>
							</h6>
						</div>
					</Col>
				</Row>
			</Grid>
			<Modal backdrop="static" show={error.length > 0 ? true : false} onHide={handleClose}>
				<Modal.Header>
					<Modal.Title>Sign In Error</Modal.Title>
				</Modal.Header>
				<Modal.Body>
					{error}
				</Modal.Body>
				<Modal.Footer>
					<Button onClick={handleClose} appearance="primary">
						OK, Got it
					</Button>
				</Modal.Footer>
			</Modal>

			{/* <Grid>
				<Grid.Row>
					<Grid.Column width="8" verticalAlign="middle">
						<div style={headerStyle}>
							<Header as="h2" icon color="purple">
								<Icon name="home" color="pink" />
								Comics Ghor
								<Header.Subheader><span style={{fontWeight: 600}}>A Canvas to Paint Your Mind Out</span></Header.Subheader>
								<Header.Subheader style={subHeaderStyle}>
									Welcome to Comics Ghor, a Bangladeshi website. The purpose of Comics Ghor is to
									create a free platform for all the Bangladeshi people, of all age, who desire to
									draw comics or write novels, stories or more.
								</Header.Subheader>
							</Header>
						</div>
					</Grid.Column>
					<Grid.Column width="8" style={{ padding: '35px' }}>
						<div>
							<Message
								attached
								header="Welcome to Comics Ghor!"
								content="Fill out the form below to sign-up for a new account"
							/>
							<Form className="attached fluid segment" onSubmit={handleSubmit} loading={checking}>
								<Form.Input
									value={email}
									onChange={handleChange('email')}
									label="Email"
									placeholder="Email"
									type="email"
								/>
								<Form.Input
									value={password}
									onChange={handleChange('password')}
									label="Password"
									placeholder="Password"
									type="password"
								/>
								<Message>
                                    <Icon color="blue" name="hand point right" size="large" />
                                    Do not share your sign-in credentials with others.
                                    </Message>
								<Button color="blue" type="submit" loading={checking}>
									Sign In
								</Button>
							</Form>
							<Message attached="bottom" warning>
								<Icon name="help" />
								Don't have an account?&nbsp;<Link href="/signup"><a>Create Account</a></Link>
							</Message>
						</div>
					</Grid.Column>
				</Grid.Row>
			</Grid>
			<Modal open={error} onClose={handleClose} basic size="tiny">
				<Header icon="browser" content="Sign-in Error" />
				<Modal.Content>{error}</Modal.Content>
				<Modal.Actions>
					<Button color="green" onClick={handleClose} inverted>
						<Icon name="checkmark" /> Got it
					</Button>
				</Modal.Actions>
			</Modal>
			<Modal open={message} onClose={pushHome} basic size="tiny">
				<Header icon="browser" content="Sign-in Successful" />
				<Modal.Content>You have signed in successfully. Do not share your sign-in credentials with others.</Modal.Content>
				<Modal.Actions>
					<Button color="green" onClick={pushHome} inverted>
						<Icon name="checkmark" /> Got it
					</Button>
				</Modal.Actions>
			</Modal> */}
		</React.Fragment>
	);
};

export default SigninComponent;
