import { useState } from 'react';
import { Menu, Segment, Icon, Input } from 'semantic-ui-react';
import Link from 'next/link';

const Topnav = () => {
    const [activeItem, activateItem] = useState('home')

    const segmentStyle = {
        borderRadius: 0
    }

    return (
        <Segment inverted style={segmentStyle}>
            <Menu inverted pointing secondary>
                <Menu.Item>
                    <Icon name='grid layout'/>
                </Menu.Item>
                <Menu.Item
                    name='home'
                    active={activeItem === 'home'}
                    onClick={() => activateItem('home')}
                >
                    <Link href="/">Home</Link>
                </Menu.Item>
                <Menu.Item
                    name='messages'
                    active={activeItem === 'messages'}
                    onClick={() => activateItem('messages')}
                />
                <Menu.Item
                    name='friends'
                    active={activeItem === 'friends'}
                    onClick={() => activateItem('friends')}
                />
                <Menu.Item position='right'>
                    <Input
                        action={{ type: 'submit', content: 'Search' }}
                        placeholder='Type to search...'
                    />
                </Menu.Item>
            </Menu>
        </Segment>
    )
}

export default Topnav;
