import { useState, useEffect } from 'react';
import { getCookie } from '../../actions/auth';
import { create, getTags, removeTag } from '../../actions/tag';
import { Form, Button, Icon, List, Grid, Message, Modal, Header } from 'semantic-ui-react';

const Tag = () => {
	const [ values, setValues ] = useState({
		name: '',
		error: false,
		success: false,
		tags: [],
		removed: false,
		reload: false
	});

	const { name, error, success, tags, removed, reload } = values;
	const token = getCookie('token');

	useEffect(
		() => {
			loadTags();
		},
		[ reload ]
	);

	const loadTags = () => {
		getTags().then((data) => {
			if (data.error) {
				console.log(data.error);
			} else {
				setValues({ ...values, tags: data });
			}
		});
	};

	const showTags = () => {
		return tags.map((c, i) => {
			return (
				<List.Item>
					<List.Content>
						<Button icon labelPosition="right" style={{ width: 180 }} onClick={() => deleteConfirm(c.slug)}>
							<Icon name="trash alternate" />
							{c.name}
						</Button>
					</List.Content>
				</List.Item>
			);
		});
	};

	const deleteConfirm = (slug) => {
		let answer = window.confirm('Are you sure you want to delete this tag?');
		if (answer) {
			deleteTag(slug);
		}
	};

	const deleteTag = (slug) => {
		removeTag(slug, token).then((data) => {
			if (data.error) {
				console.log(data.error);
			} else {
				setValues({ ...values, error: false, success: false, name: '', removed: !removed, reload: !reload });
			}
		});
	};

	const clickSubmit = (e) => {
		e.preventDefault();
		create({ name }, token).then((data) => {
			if (data.error) {
				setValues({ ...values, error: data.error, success: false });
			} else {
				setValues({ ...values, error: false, success: true, name: '', removed: false, reload: !reload });
			}
		});
	};

	const handleChange = (e) => {
		setValues({ ...values, name: e.target.value, error: false, success: false, removed: '' });
	};

	const newTagForm = () => (
		<div>
			<Form onSubmit={clickSubmit}>
				<Form.Group>
					<Form.Input placeholder="Add a new tag" value={name} onChange={handleChange} />
					<Form.Button content="Add" icon="plus" type="submit" />
				</Form.Group>
			</Form>
		</div>
	);

	const handleClose = () => {
		setValues({ ...values, error: false, success: false, removed: false});
	};

	return (
		<React.Fragment>
			<Grid>
				<Grid.Row>
					<Grid.Column>
						<Message
							info
							icon="inbox"
							header="Tags"
							content="Press enter or click add to add a tag. Click a tag to remove it."
						/>
						{newTagForm()}
						<List  verticalAlign='middle'>
							{showTags()}
						</List>
					</Grid.Column>
				</Grid.Row>
			</Grid>
			<Modal open={error} onClose={handleClose} basic size="tiny">
				<Header icon="browser" content="Error" />
				<Modal.Content><h3>Tag already exists!</h3></Modal.Content>
				<Modal.Actions>
					<Button color="green" onClick={handleClose} inverted>
						<Icon name="checkmark" /> Got it
					</Button>
				</Modal.Actions>
			</Modal>
			<Modal open={success} onClose={handleClose} basic size="tiny">
				<Header icon="browser" content="Successful" />
				<Modal.Content><h3>Tag created successfully!</h3></Modal.Content>
				<Modal.Actions>
					<Button color="green" onClick={handleClose} inverted>
						<Icon name="checkmark" /> Got it
					</Button>
				</Modal.Actions>
			</Modal>
			<Modal open={removed} onClose={handleClose} basic size="tiny">
				<Header icon="browser" content="Successful" />
				<Modal.Content><h3>Tag removed successfully!</h3></Modal.Content>
				<Modal.Actions>
					<Button color="green" onClick={handleClose} inverted>
						<Icon name="checkmark" /> Got it
					</Button>
				</Modal.Actions>
			</Modal>
		</React.Fragment>
	);
};

export default Tag;
